INSERT INTO `contrexx_module_newsletter` (`id`, `subject`, `template`, `content`, `attachment`, `priority`, `sender_email`, `sender_name`, `return_path`, `smtp_server`, `status`, `count`, `recipient_count`, `date_create`, `date_sent`, `tmp_copy`) VALUES (2,'Neue Homepage',1,'[[salutation]] [[lastname]]<br />\r\n<br />\r\nGerne machen wir Sie auf unsere neue Website aufmerksam. Der Internetauftritt wurde komplett erneuert und aufgefrischt. Sie finden nun alle Informationen &uuml;bersichtlich dargestellt und ein modernisiertes Design.<br />\r\n<br />\r\nF&uuml;r unser Webprojekt haben wir <a  href=\"https://www.cloudrexx.com\" rel=\"newsletter_link_1\" target=\"_blank\">Cloudrexx</a> als Web Content Management System ausgew&auml;hlt. Cloudrexx beinhaltet alle wichtigen Anwendungen, die eine Website heute braucht. Die Inhalte und Funktionen k&ouml;nnen jederzeit mit kleinem Aufwand bearbeitet und aufgefrischt werden, um stets eine aktuelle und ansprechende Webseite zu pr&auml;sentieren.<br />\r\n<br />\r\n<br />\r\nIhr MaxMuster-Team','0',3,'info@example.com','admin','info@maxmusterag.com',0,0,0,0,1348220511,0,0);
