INSERT INTO `contrexx_content` (`id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `expertmode`) VALUES (807,'<script type=\"text/javascript\">\r\n<!--\r\nfunction toggle(target){\r\n    obj = document.getElementById(target);\r\n    obj.style.display = (obj.style.display==\'none\') ? \'inline\' : \'none\';  \r\n    if (obj.style.display==\'none\' && target == \'hiddenSearch\'){\r\n         document.getElementById(\'searchCheck\').value = \'norm\';\r\n    }else if(obj.style.display==\'inline\' && target == \'hiddenSearch\'){\r\n         document.getElementById(\'searchCheck\').value = \'exp\';\r\n    }\r\n}\r\n\r\nfunction CheckForm() {\r\n  var errorMsg = \"\";\r\n  with( document.formEntry ) {\r\n  	if (type.value == \"\") {\r\n    	errorMsg = errorMsg + \"- {TXT_MARKET_TYPE}\\n\";\r\n    }\r\n    if (title.value == \"\") {\r\n        errorMsg = errorMsg + \"- {TXT_MARKET_TITLE_ENTRY}\\n\";\r\n    } \r\n    if (description.value == \"\") {\r\n        errorMsg = errorMsg + \"- {TXT_MARKET_DESCRIPTION}\\n\";\r\n    }   \r\n    if (cat.value == \"\") {\r\n        errorMsg = errorMsg + \"- {TXT_MARKET_CATEGORIE}\\n\";\r\n    } \r\n    if (userdetails.value == \"\") {\r\n        errorMsg = errorMsg + \"- {TXT_MARKET_USER_DETAIL}\\n\";\r\n    }\r\n    if (name.value == \"\") {\r\n        errorMsg = errorMsg + \"- {TXT_MARKET_NAME}\\n\";\r\n    }\r\n    if (email.value == \"\") {\r\n        errorMsg = errorMsg + \"- {TXT_MARKET_EMAIL}\\n\";\r\n    }else{\r\n    	if (!matchType(\'^[_a-zA-Z0-9-]+(.[_a-zA-Z0-9-]+)*@[a-zA-Z0-9-]+(.[a-zA-Z0-9-]+)*.(([0-9]{1,3})|([a-zA-Z]{2,3})|(aero|coop|info|museum|name))$\', email.value)){\r\n    		errorMsg = errorMsg + \"- {TXT_MARKET_EMAIL}\\n\";\r\n    	}\r\n    }\r\n    \r\n    if (errorMsg != \"\") \r\n    {\r\n        alert (\"{TXT_MARKET_FIELDS_REQUIRED}n{TXT_MARKET_THOSE_FIELDS_ARE_EMPTY}\\n\" + errorMsg);       \r\n        return false;\r\n    }else{\r\n    	return true;\r\n    }\r\n  }\r\n}\r\n\r\nfunction matchType(pattern, value) {\r\n	var reg = new RegExp(pattern);\r\n	if (value.match(reg)) {\r\n		return true;\r\n	}\r\n	return false;\r\n} \r\n\r\n-->\r\n</script>\r\n<div id=\"market\">\r\n	<form name=\"formEntry\" enctype=\"multipart/form-data\" method=\"post\" action=\"?section=market&cmd=edit\" onsubmit=\"return CheckForm()\">\r\n	<input type=\"hidden\" name=\"id\" value=\"{MARKET_ENTRY_ID}\" >\r\n	<input type=\"hidden\" name=\"picOld\" value=\"{MARKET_ENTRY_PICTURE_OLD}\" >\r\n		<table width=\"100%\" cellspacing=\"0\" cellpadding=\"2\" border=\"0\" align=\"top\" class=\"adminlist\">\r\n			<tr>\r\n				<td nowrap=\"nowrap\">{TXT_MARKET_TYPE}<font color=\"red\"> *</font></td>\r\n				<td>\r\n					<input type=\"radio\" name=\"type\" value=\"offer\" {MARKET_ENTRY_TYPE_OFFER} />&nbsp;{TXT_MARKET_OFFER}\r\n					<input type=\"radio\" name=\"type\" value=\"search\" {MARKET_ENTRY_TYPE_SEARCH} />&nbsp;{TXT_MARKET_SEARCH}\r\n				</td>\r\n			</tr>\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_MARKET_TITLE_ENTRY}<font color=\"red\"> *</font></td>		\r\n				<td><input type=\"text\" name=\"title\" style=\"width: 300px;\" maxlength=\"100\" value=\"{MARKET_ENTRY_TITLE}\" ></td>\r\n			</td>		\r\n			</tr>\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_MARKET_DESCRIPTION}<font color=\"red\"> *</font></td>\r\n				<td><textarea name=\"description\" rows=\"5\"  style=\"width: 300px; overflow: auto;\">{MARKET_ENTRY_DESCRIPTION}</textarea></td>\r\n			</tr>\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_MARKET_PICTURE}</td>		\r\n				<td>{MARKET_ENTRY_PICTURE}<input type=\"file\" name=\"pic\" size=\"38\" style=\"width: 304px;\" maxlength=\"100\"></td>\r\n			</td>	\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_MARKET_CATEGORIE}<font color=\"red\"> *</font></td>		\r\n				<td><select name=\"cat\"  style=\"width: 305px;\">{MARKET_CATEGORIES}</select></td>\r\n			</td>	\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_MARKET_PRICE}</td>		\r\n				<td><input type=\"text\" name=\"price\" style=\"width: 98px;\" maxlength=\"100\" value=\"{MARKET_ENTRY_PRICE}\">&nbsp;&nbsp;<input type=\"checkbox\" name=\"forfree\" value=\"1\"  {MARKET_ENTRY_FORFREE} />&nbsp;{TXT_MARKET_FOR_FREE}&nbsp;<input type=\"checkbox\" name=\"agreement\" value=\"1\" {MARKET_ENTRY_AGREEMENT} />&nbsp;{TXT_MARKET_AGREEMENT}</td>\r\n			</td>\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_MARKET_NAME}<font color=\"red\"> *</font></td>		\r\n				<td><input type=\"text\" name=\"name\" style=\"width: 300px;\" maxlength=\"100\" value=\"{MARKET_ENTRY_NAME}\"></td>\r\n			</td>\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_MARKET_EMAIL}<font color=\"red\"> *</font></td>		\r\n				<td><input type=\"text\" name=\"email\" style=\"width: 300px;\" maxlength=\"100\" value=\"{MARKET_ENTRY_EMAIL}\"></td>\r\n			</td>\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_MARKET_USER_DETAIL}<font color=\"red\"> *</font></td>		\r\n				<td>\r\n					<input type=\"radio\" name=\"userdetails\" value=\"1\" {MARKET_ENTRY_USERDETAILS_ON} />&nbsp;{TXT_MARKET_DETAIL_SHOW}\r\n					<input type=\"radio\" name=\"userdetails\" value=\"0\" {MARKET_ENTRY_USERDETAILS_OFF} />&nbsp;{TXT_MARKET_DETAIL_HIDE}\r\n				</td>\r\n			</td>\r\n					<!-- BEGIN spez_fields -->\r\n			<tr valign=\"top\" >\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_MARKET_SPEZ_FIELD_NAME}</td>\r\n				<td>{MARKET_SPEZ_FIELD_INPUT}</td>\r\n			</td>\r\n			<!-- END spez_fields -->\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\"><br /></td>		\r\n				<td>\r\n					<input type=\"submit\" name=\"submitEntry\" value=\"{TXT_MARKET_SAVE}\" />\r\n				</td>\r\n			</td>\r\n	   </table>\r\n	</form>\r\n</div>','Inserat editieren','Inserat editieren','Inserat editieren','Inserat editieren','index','','','y');
