ory -->\r\n	</div>\r\n	<!-- END category_list -->\r\n	\r\n	<!-- BEGIN category_show -->\r\n	<div class=\"char_list\">{MEMBERDIR_CHAR_LIST}</div>\r\n	<div class=\"search_form\">\r\n		<form action=\"index.php?section=memberdir\" method=\"get\">\r\n			<input type=\"hidden\" name=\"search\" value=\"search\" /> <input type=\"hidden\" name=\"section\" value=\"memberdir\" /> <input onclick=\"this.value=\'\'\" name=\"keyword\" value=\"{MEMBERDIR_KEYWORD}\" /> {MEMBERDIR_DROPDOWN} <input type=\"submit\" value=\"{MEMBERDIR_SEARCH}\" />\r\n		</form>\r\n	</div>\r\n	<div class=\"adress_list\">\r\n		<table cellspacing=\"0\">\r\n			<tbody>\r\n				<tr>\r\n					<th>Name</th>\r\n					<th>Adresse</th>\r\n				</tr>\r\n				<!-- BEGIN memberdir_row -->\r\n				<tr class=\"row{MEMBERDIR_ROW}\">\r\n					<td valign=\"top\"><a title=\"{FIELD_NAME} {FIELD_VORNAME}\" href=\"?section=memberdir&amp;mid={MEMBERDIR_ID}\"><strong>{FIELD_NAME} {FIELD_VORNAME}</strong></a></td>\r\n					<td valign=\"top\">{FIELD_ADRESSE}<br />{FIELD_PLZ} {FIELD_ORT}</td>\r\n				</tr>\r\n				<!-- END memberdir_row -->\r\n			</tbody>\r\n		</table>\r\n	</div>\r\n	<div class=\"paging\">{MEMBERDIR_PAGING}</div>\r\n	<!-- END category_show -->\r\n	\r\n	<!-- BEGIN memberdir_detail_view -->\r\n	<div class=\"detail_list\">\r\n		<table cellspacing=\"0\">\r\n			<tbody>\r\n				<tr class=\"row1\">\r\n					<td valign=\"top\" width=\"50%\">{MEMBERDIR_PIC1}<br /></td>\r\n					<td  valign=\"top\" width=\"50%\">{MEMBERDIR_PIC2}<br /></td>\r\n				</tr>\r\n			</tbody>\r\n		</table>\r\n		<table cellspacing=\"0\">\r\n			<tbody>\r\n				<tr>\r\n					<th>Personalien</th>\r\n					<th><br /></th>\r\n				</tr>\r\n				<!-- BEGIN row -->\r\n				<tr class=\"row1\">\r\n					<td valign=\"top\" width=\"150\"><strong>{MEMBERDIR_FIELD_NAME}:</strong></td>\r\n					<td  valign=\"top\">{MEMBERDIR_FIELD_VALUE}</td>\r\n				</tr>\r\n				<!-- END row -->\r\n			</tbody>\r\n		</table>\r\n	</div>\r\n	<div class=\"back\"><a title=\"Zur&uuml;ck\" href=\"javascript:hi