INSERT INTO `contrexx_content` (`id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `expertmode`) VALUES (938,'<!-- BEGIN signForm -->\r\n{CALENDAR_FIELD_JS}\r\n<form name=\"signForm\" id=\"signForm\" onsubmit=\"return checkFields();\" method=\"post\" action=\"index.php?section=calendar&amp;cmd=sign\">\r\n<input type=\"hidden\" name=\"id\" value=\"{CALENDAR_NOTE_ID}\" />\r\n<input type=\"hidden\" name=\"userid\" value=\"{CALENDAR_USER_ID}\" />\r\n    <table border=\"0\">\r\n        <tbody>\r\n            <tr>\r\n                <td style=\"width: 120px;\">An- \\ Abmeldung</td>\r\n                <td><input type=\"radio\" name=\"type\" value=\"1\" checked=\"checked\" />&nbsp;Anmeldung&nbsp;&nbsp;&nbsp;<input type=\"radio\" name=\"type\" value=\"0\" />&nbsp;Abmeldung</td>\r\n            </tr>\r\n            <!-- BEGIN calendarRegFields -->\r\n            <tr>\r\n                <td>{CALENDAR_FIELD_NAME}</td>\r\n                <td>{CALENDAR_FIELD_INPUT}</td>\r\n            </tr>\r\n            <!-- END calendarRegFields -->\r\n            <tr>\r\n                <td><br /></td>\r\n                <td><input type=\"submit\" name=\"submit\" value=\"Absenden\" /></td>\r\n            </tr>\r\n        </tbody>\r\n    </table>\r\n</form>\r\n<!-- END signForm -->\r\n<!-- BEGIN signStatus -->\r\n{CALENDAR_REGISTRATIONS_STATUS}<br />\r\n<br />\r\n<a href=\"?section=calendar\">{TXT_CALENDAR_BACK}</a>\r\n<!-- END signStatus -->','An- \\ Abmelden','An- \\ Abmelden','An- \\ Abmelden','An- \\ Abmelden','index','','','y');
INSERT INTO `contrexx_content` (`id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `expertmode`) VALUES (940,'{CONTACT_FEEDBACK_TEXT}\n<!-- BEGIN formText --><p>Bitte lassen Sie uns wissen wie wir Ihnen helfen k&ouml;nnen, wir werden uns mit Ihnen so schnell wie m&ouml;glich in Verbindung setzen.</p><!-- END formText --><br /><br />\n<div id=\"contactFormError\" style=\"color: red; display: none;\">\nBitte &uuml;berpr&uuml;fen Sie ihre Eingabe!\n</div>\n<br />\n<!-- BEGIN contact_form -->\n<fieldset id=\"contactFrame\">\n<legend>Kontaktformular</legend>\n<form action=\"index.php?section=contact&amp;cmd=1\" \nmethod=\"post\" enctype=\"multipart/form-data\" onsubmit=\"return checkAllFields();\" id=\"contactForm\" class=\"contactForm\">\n<p> <label for=\"contactFormFieldId_1\">Name <strong class=\"is_required\">*</strong></label>\n<input class=\"contactFormClass_text\" id=\"contactFormFieldId_1\" type=\"text\" name=\"contactFormField_1\" value=\"{1_VALUE}\" />\n</p>\n<p> <label for=\"contactFormFieldId_2\">Firma </label>\n<input class=\"contactFormClass_text\" id=\"contactFormFieldId_2\" type=\"text\" name=\"contactFormField_2\" value=\"{2_VALUE}\" />\n</p>\n<p> <label for=\"contactFormFieldId_3\">Strasse </label>\n<input class=\"contactFormClass_text\" id=\"contactFormFieldId_3\" type=\"text\" name=\"contactFormField_3\" value=\"{3_VALUE}\" />\n</p>\n<p> <label for=\"contactFormFieldId_4\">PLZ </label>\n<input class=\"contactFormClass_text\" id=\"contactFormFieldId_4\" type=\"text\" name=\"contactFormField_4\" value=\"{4_VALUE}\" />\n</p>\n<p> <label for=\"contactFormFieldId_5\">Stadt </label>\n<input class=\"contactFormClass_text\" id=\"contactFormFieldId_5\" type=\"text\" name=\"contactFormField_5\" value=\"{5_VALUE}\" />\n</p>\n<p> <label for=\"contactFormFieldId_6\">Land </label>\n<input class=\"contactFormClass_text\" id=\"contactFormFieldId_6\" type=\"text\" name=\"contactFormField_6\" value=\"{6_VALUE}\" />\n</p>\n<p> <label for=\"contactFormFieldId_7\">Telefon </label>\n<input class=\"contactFormClass_text\" id=\"contactFormFieldId_7\" type=\"text\" name=\"contactFormField_7\" value=\"{7_VALUE}\" />\n</p>\n<p> <label for=\"contactFormFieldId_8\">Fax </label>\n<input class=\"contactFormClass_text\" id=\"contactFormFieldId_8\" type=\"text\" name=\"contactFormField_8\" value=\"{8_VALUE}\" />\n</p>\n<p> <label for=\"contactFormFieldId_9\">E-Mail <strong class=\"is_required\">*</strong></label>\n<input class=\"contactFormClass_text\" id=\"contactFormFieldId_9\" type=\"text\" name=\"contactFormField_9\" value=\"{9_VALUE}\" />\n</p>\n<p> <label for=\"contactFormFieldId_10\">Kommentar </label>\n<textarea class=\"contactFormClass_textarea\" name=\"contactFormField_10\" id=\"contactFormFieldId_10\" rows=\"5\" cols=\"20\">{10_VALUE}</textarea>\n</p>\n<p> <label for=\"contactFormFieldId_11\">Dateiupload </label>\n<input class=\"contactFormClass_file\" id=\"contactFormFieldId_11\" type=\"file\" name=\"contactFormField_11\" />\n</p>\n<!-- BEGIN contact_form_captcha -->\n<div style=\"color: red;\">{CONTACT_CAPTCHA_ERROR}</div>\n<p>\n{TXT_CONTACT_CAPTCHA_DESCRIPTION}<br />\n</p>\n<p><span>CAPTCHA</span><img class=\"captcha\" src=\"{CONTACT_CAPTCHA_URL}\" alt=\"{CONTACT_CAPTCHA_ALT}\" />\n<input id=\"contactFormCaptcha\" type=\"text\" name=\"contactFormCaptcha\" /><br />\n<input type=\"hidden\" name=\"contactFormCaptchaOffset\" value=\"{CONTACT_CAPTCHA_OFFSET}\" />\n</p>\n<!-- END contact_form_captcha -->\n<p>\n<input class=\"contactFormClass_button\" type=\"reset\" value=\"L&ouml;schen\" /> <input class=\"contactFormClass_button\" type=\"submit\" name=\"submitContactForm\" value=\"Absenden\" />\n</p>\n</form>\n</fieldset>\n<!-- END contact_form -->\n<script src=\"lib/datepickercontrol/datepickercontrol.js\" type=\"text/javascript\"></script>\n<script type=\"text/javascript\">\n/* <![CDATA[ */\nfields = new Array();\nfields[1] = Array(\n	\'Name\',\n	1,\n	\'.*\',\n	\'text\');\nfields[2] = Array(\n	\'Firma\',\n	0,\n	\'.*\',\n	\'text\');\nfields[3] = Array(\n	\'Strasse\',\n	0,\n	\'.*\',\n	\'text\');\nfields[4] = Array(\n	\'PLZ\',\n	0,\n	\'.*\',\n	\'text\');\nfields[5] = Array(\n	\'Stadt\',\n	0,\n	\'.*\',\n	\'text\');\nfields[6] = Array(\n	\'Land\',\n	0,\n	\'.*\',\n	\'text\');\nfields[7] = Array(\n	\'Telefon\',\n	0,\n	\'.*\',\n	\'text\');\nfields[8] = Array(\n	\'Fax\',\n	0,\n	\'.*\',\n	\'text\');\nfields[9] = Array(\n	\'E-Mail\',\n	1,\n	\'^[_a-zA-Z0-9-]+(\\\\.[_a-zA-Z0-9-]+)*@[a-zA-Z0-9-]+(\\\\.[a-zA-Z0-9-]+)*\\\\.(([0-9]{1,3})|([a-zA-Z]{2,3})|(aero|coop|info|museum|name))$\',\n	\'text\');\nfields[10] = Array(\n	\'Kommentar\',\n	0,\n	\'.*\',\n	\'textarea\');\nfields[11] = Array(\n	\'Dateiupload\',\n	0,\n	\'.*\',\n	\'file\');\nfunction checkAllFields() {\n	var isOk = true;\n\n	for (var field in fields) {\n		var type = fields[field][3];\n		if (type == \'text\' || type == \'file\' || type == \'password\' || type == \'textarea\') {\n			value = document.getElementsByName(\'contactFormField_\' + field)[0].value;\n			if (value == \"\" && isRequiredNorm(fields[field][1], value)) {\n				isOk = false;\n				document.getElementsByName(\'contactFormField_\' + field)[0].style.border = \"red 1px solid\";\n			} else if (value != \"\" && !matchType(fields[field][2], value)) {\n				isOk = false;\n				document.getElementsByName(\'contactFormField_\' + field)[0].style.border = \"red 1px solid\";\n			} else {\n				document.getElementsByName(\'contactFormField_\' + field)[0].style.borderColor = \'\';\n			}\n		} else if (type == \'checkbox\') {\n			if (!isRequiredCheckbox(fields[field][1], field)) {\n				isOk = false;\n			}\n		} else if (type == \'checkboxGroup\') {\n			if (!isRequiredCheckBoxGroup(fields[field][1], field)) {\n				isOk = false;\n			}\n		} else if (type == \'radio\') {\n			if (!isRequiredRadio(fields[field][1], field)) {\n				isOk = false;\n			}\n		}\n	}\n\n	if (!isOk) {\n		document.getElementById(\'contactFormError\').style.display = \"block\";\n	}\n	return isOk;\n}\nfunction isRequiredNorm(required, value) {\n	if (required == 1) {\n		if (value == \"\") {\n			return true;\n		}\n	}\n	return false;\n}\nfunction matchType(pattern, value) {\n	var reg = new RegExp(pattern);\n	if (value.match(reg)) {\n		return true;\n	}\n	return false;\n}\nfunction isRequiredCheckbox(required, field) {\n	if (required == 1) {\n		if (!document.getElementsByName(\'contactFormField_\' + field)[0].checked) {\n			document.getElementsByName(\'contactFormField_\' + field)[0].style.border = \"red 1px solid\";\n			return false;\n		}\n	}\n	document.getElementsByName(\'contactFormField_\' + field)[0].style.borderColor = \'\';\n\n	return true;\n}\nfunction isRequiredCheckBoxGroup(required, field) {\n	if (required == true) {\n		var boxes = document.getElementsByName(\'contactFormField_\' + field + \'[]\');\n		var checked = false;\n		for (var i = 0; i < boxes.length; i++) {\n 			if (boxes[i].checked) {\n				checked = true;\n			}\n		}\n		if (checked) {\n			setListBorder(\'contactFormField_\' + field + \'[]\', false);\n			return true;\n		} else {\n			setListBorder(\'contactFormField_\' + field + \'[]\', \'1px red solid\');\n			return false;\n		}\n	} else {\n		return true;\n	}\n}\nfunction isRequiredRadio(required, field) {\n	if (required == 1) {\n		var buttons = document.getElementsByName(\'contactFormField_\' + field);\n		var checked = false;\n		for (var i = 0; i < buttons.length; i++) {\n			if (buttons[i].checked) {\n				checked = true;\n			}\n		}\n		if (checked) {\n			setListBorder(\'contactFormField_\' + field, false);\n			return true;\n		} else {\n			setListBorder(\'contactFormField_\' + field, \'1px red solid\');\n			return false;\n		}\n	} else {\n		return true;\n	}\n}\nfunction setListBorder(field, borderColor) {\n	var boxes = document.getElementsByName(field);\n	for (var i = 0; i < boxes.length; i++) {\n		if (borderColor) {\n			boxes[i].style.border = borderColor;\n		} else {\n			boxes[i].style.borderColor = \'\';\n		}\n	}\n}\n/* ]]> */\n</script>\n','Kontakt','Kontakt','Kontakt','Kontakt','index','','','n');
