INSERT INTO `contrexx_content` (`id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `expertmode`) VALUES (948,'<!-- start module_podcast_modify_medium.html -->\r\n\r\n<!-- BEGIN podcastThanks -->\r\n<div style=\"color: #029202;\">\r\nVielen Dank, das Video wurde registriert.<br />\r\nEs wird freigeschaltet, sobald es erfolgreich validiert wurde.<br />\r\n<a href=\"index.php?section=podcast\" title=\"podcast\">Zurck zum Podcast-Index</a>\r\n</div>\r\n<!-- END podcastThanks -->\r\n\r\n<!-- BEGIN podcastForm -->\r\n<form action=\"index.php?section=podcast&amp;cmd=modifyMedium&amp;id=0\" method=\"post\">\r\n	<table border=\"0\" cellpadding=\"3\" cellspacing=\"0\" width=\"100%\" class=\"adminlist\">\r\n		<tbody>\r\n			<tr>\r\n				<th colspan=\"4\">{PODCAST_MODIFY_TITLE}</th>\r\n			</tr>\r\n			<tr class=\"row1\">\r\n				<td width=\"15%\">{TXT_PODCAST_TITLE}</td>\r\n				<td width=\"85%\" colspan=\"3\"><input type=\"text\" value=\"{PODCAST_MEDIUM_TITLE}\" name=\"podcast_medium_title\" maxlength=\"255\" style=\"width:99%;\" /></td>\r\n			</tr>\r\n			<tr class=\"row2\">\r\n				<td>{TXT_PODCAST_AUTHOR}</td>\r\n				<td colspan=\"3\"><input type=\"text\" value=\"{PODCAST_MEDIUM_AUTHOR}\" name=\"podcast_medium_author\" maxlength=\"255\" style=\"width:99%;\" /></td>\r\n			</tr>\r\n			<tr class=\"row1\" style=\"vertical-align:top;\">\r\n				<td>{TXT_PODCAST_DESCRIPTION}</td>\r\n				<td colspan=\"3\"><textarea name=\"podcast_medium_description\" cols=\"150\" rows=\"10\" style=\"width:99%; height:100px;\">{PODCAST_MEDIUM_DESCRIPTION}</textarea></td>\r\n			</tr>\r\n			<tr class=\"row2\">\r\n				<td>{TXT_PODCAST_SOURCE}</td>\r\n				<td colspan=\"3\"><input type=\"hidden\" value=\"{PODCAST_MEDIUM_SOURCE_URL}\" name=\"podcast_medium_source\" /><a href=\"{PODCAST_MEDIUM_SOURCE}\" target=\"_blank\">{PODCAST_MEDIUM_SOURCE}</a></td>\r\n			</tr>\r\n			<tr class=\"row1\">\r\n				<td>{TXT_PODCAST_TEMPLATE}</td>\r\n				<td colspan=\"3\">{PODCAST_MEDIUM_TEMPLATE_MENU}</td>\r\n			</tr>\r\n			<tr class=\"row2\" style=\"vertical-align:top;\">\r\n				<td>{TXT_PODCAST_DIMENSIONS}</td>\r\n				<td colspan=\"3\">\r\n					<input type=\"text\" value=\"{PODCAST_MEDIUM_WIDTH}\" name=\"podcast_medium_width\" style=\"width:50px; text-align:right; margin-bottom:1px;\" /> {TXT_PODCAST_PIXEL_WIDTH}<br />\r\n					<input type=\"text\" value=\"{PODCAST_MEDIUM_HEIGHT}\" name=\"podcast_medium_height\" style=\"width:50px; text-align:right;\" /> {TXT_PODCAST_PIXEL_HEIGHT}\r\n				</td>\r\n			</tr>\r\n			<tr class=\"row1\">\r\n				<td>{TXT_PODCAST_PLAYLENGHT}</td>\r\n				<td colspan=\"3\"><input type=\"text\" value=\"{PODCAST_MEDIUM_PLAYLENGHT}\" name=\"podcast_medium_playlenght\" style=\"width:50px; text-align:right;\" /> {TXT_PODCAST_PLAYLENGHT_FORMAT}</td>\r\n			</tr>\r\n			<tr class=\"row2\">\r\n				<td>{TXT_PODCAST_FILESIZE}</td>\r\n				<td colspan=\"3\"><input {PODCAST_MEDIUM_YOUTUBE_DISABLED} type=\"text\" value=\"{PODCAST_MEDIUM_FILESIZE}\" name=\"podcast_medium_filesize\" style=\"width:50px; text-align:right;\" /> {TXT_PODCAST_BYTES}</td>\r\n			</tr>\r\n			<tr class=\"row1\">\r\n				<td>{TXT_PODCAST_THUMBNAIL}</td>\r\n				<td colspan=\"3\"><img id=\"podcast_thumbnail\"  style=\"border: none;\" src=\"{PODCAST_MEDIUM_THUMBNAIL_SRC}\" title=\"preview thumbnail\" alt=\"preview thumbnail\" /></td>\r\n			</tr>\r\n			<tr class=\"row2\" style=\"vertical-align:top;\">\r\n				<td width=\"15%\">{TXT_PODCAST_CATEGORIES}</td>\r\n				<td width=\"20%\">\r\n					<!-- BEGIN podcast_medium_associated_category_0 -->\r\n						<input type=\"checkbox\" name=\"podcast_medium_associated_category[{PODCAST_CATEGORY_ID}]\" id=\"podcast_medium_associated_category_{PODCAST_CATEGORY_ID}\" value=\"1\" checked=\"checked\" /> <a href=\"index.php?cmd=podcast&amp;categoryId={PODCAST_CATEGORY_ID}\" target=\"_blank\" title=\"{PODCAST_SHOW_MEDIA_OF_CATEGORY_TXT}\">{PODCAST_CATEGORY_NAME}</a><br />\r\n					<!-- END podcast_medium_associated_category_0 -->\r\n				</td>\r\n				<td width=\"20%\">\r\n					<!-- BEGIN podcast_medium_associated_category_1 -->\r\n						<input type=\"checkbox\" name=\"podcast_medium_associated_category[{PODCAST_CATEGORY_ID}]\" id=\"podcast_medium_associated_category_{PODCAST_CATEGORY_ID}\" value=\"1\" checked=\"checked\" /> <a href=\"index.php?cmd=podcast&amp;categoryId={PODCAST_CATEGORY_ID}\" target=\"_blank\" title=\"{PODCAST_SHOW_MEDIA_OF_CATEGORY_TXT}\">{PODCAST_CATEGORY_NAME}</a><br />\r\n					<!-- END podcast_medium_associated_category_1 -->\r\n				</td>\r\n				<td width=\"45%\">\r\n					<!-- BEGIN podcast_medium_associated_category_2 -->\r\n						<input type=\"checkbox\" name=\"podcast_medium_associated_category[{PODCAST_CATEGORY_ID}]\" id=\"podcast_medium_associated_category_{PODCAST_CATEGORY_ID}\" value=\"1\" checked=\"checked\" /> <a href=\"index.php?cmd=podcast&amp;categoryId={PODCAST_CATEGORY_ID}\" target=\"_blank\" title=\"{PODCAST_SHOW_MEDIA_OF_CATEGORY_TXT}\">{PODCAST_CATEGORY_NAME}</a><br />\r\n					<!-- END podcast_medium_associated_category_2 -->\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n	<br />\r\n	<input type=\"submit\" name=\"podcast_medium_save\" value=\"{TXT_PODCAST_SAVE}\" />\r\n	<input type=\"hidden\" name=\"podcast_medium_thumbnail\" id=\"podcast_medium_thumbnail\" value=\"{PODCAST_MEDIUM_THUMBNAIL_SRC}\">\r\n	<input type=\"hidden\" name=\"podcast_youtubeID\" id=\"podcast_youtubeID\" value=\"{PODCAST_MEDIUM_YOUTUBE_ID}\">\r\n</form>\r\n<script type=\"text/javascript\">\r\n//<![CDATA[\r\n 	var elInputThumbnailPath = document.getElementById(\"podcast_medium_thumbnail\");\r\n	var elImgThumbnail = document.getElementById(\"podcast_thumbnail\");\r\n	var thumbSizeMax = 90;\r\n\r\n	var openBrowser = function openBrowser(url, attrs) {\r\n		if(elInputThumbnailPath.value != \'\'){\r\n			pathRegExp =/\\/images\\/content\\/(.+)\\/.+/;\r\n			pathRegExp.exec(elInputThumbnailPath.value);\r\n			url=\'index.php?cmd=fileBrowser&standalone=true&type=files&path=/\'+RegExp.$1;\r\n		}\r\n		try{\r\n			if(!browserPopup.closed){\r\n				return browserPopup.focus();\r\n			}\r\n		}catch(e){}\r\n		if (!window.focus){\r\n			return true;\r\n		}\r\n		browserPopup = window.open(url, \"\", attrs);\r\n		browserPopup.focus();\r\n		return false;\r\n	}\r\n\r\n	var SetUrl = function(url, width, height, alt) {\r\n		elInputThumbnailPath.setAttribute(\'value\', url);\r\n		fileRegExp = /.+\\/(.+)$/;\r\n		fileRegExp.exec(url);\r\n		elTxt = document.createTextNode(RegExp.$1);\r\n		elImgThumbnail.src = url;\r\n		setSize(width, height);\r\n	}\r\n\r\n	var setSize = function (width, height){\r\n		if(width > thumbSizeMax || height > thumbSizeMax){\r\n			if(width > height){\r\n					fact = thumbSizeMax / width;\r\n					elImgThumbnail.style.width = width*fact+\'px\';\r\n					elImgThumbnail.style.height = height*fact+\'px\';\r\n			}else{\r\n					fact = thumbSizeMax / height;\r\n					elImgThumbnail.style.height = height*fact+\'px\';\r\n					elImgThumbnail.style.width = width*fact+\'px\';\r\n			}\r\n		}else{\r\n			elImgThumbnail.style.height = height+\'px\';\r\n			elImgThumbnail.style.width = width+\'px\';\r\n		}\r\n	}\r\n	tmp = window.onload;\r\n	if(tmp == null){\r\n		tmp = function(){};\r\n	}\r\n	window.onload = function(){\r\n		tmp();\r\n		setSize(parseInt(elImgThumbnail.offsetWidth), parseInt(elImgThumbnail.offsetHeight));\r\n	}\r\n\r\n\r\n\r\n//]]>\r\n</script>\r\n<!-- END podcastForm -->\r\n<!-- end module_podcast_modify_medium.html -->','Video hinzufgen','Video hinzufgen','Video hinzufgen','Video hinzufgen','index','','','y');
