INSERT INTO `contrexx_module_news` (`id`, `date`, `title`, `text`, `redirect`, `source`, `url1`, `url2`, `catid`, `lang`, `userid`, `startdate`, `enddate`, `status`, `validated`, `teaser_only`, `teaser_frames`, `teaser_text`, `teaser_show_link`, `teaser_image_path`, `changelog`) VALUES (2,1177190696,'Authorisierte Contrexx Vertriebspartner bringen Ihre Webprojekte auf Trab!','Die im deutschsprachigen Raum weit verbreitete Contrexx&reg; Web Content Management System Software verf&uuml;gt &uuml;ber ein umfangreiches Vertriebs- und Dienstleistungsnetz von selbst&auml;ndigen Agenturen. Diese Agenturen beraten Sie gerne ausf&uuml;hrlich und fachgerecht zu allen Themen rund um das Internet. <br />\r\n<br />\r\nW&auml;hlen Sie hier eine authorisierte Agentur f&uuml;r ein kostenloses und unverbindliches Angebot zur Erstellung ihres professionellen Internet-Auftritts mit dem Contrexx&reg; WCMS in ihrer Region. <br />\r\n<br />\r\n<a title=\"Schweiz\" href=\"http://www.contrexx.com/index.php?section=memberdir&amp;id=1\">Schweiz</a><br />\r\n<a title=\"Deutschland\" href=\"http://www.contrexx.com/index.php?section=memberdir&amp;id=2\">Deutschland</a><br />\r\n<a title=\"&Ouml;sterreich\" href=\"http://www.contrexx.com/index.php?section=memberdir&amp;id=43\">&Ouml;sterreich</a><br />\r\n<a title=\"D&auml;nemark\" href=\"http://www.contrexx.com/index.php?section=memberdir&amp;id=3\">D&auml;nemark</a> <!-- end content.html -->\r\n<div style=\"height: 25px;\">&nbsp;</div>','','','','',2,1,1,'0000-00-00','0000-00-00',1,'1','0','','Die im deutschsprachigen Raum weit verbreitete Contrexx Web Content Management System Software verfgt ber ein umfangreiches Vertriebs- und Dienstleistungsnetz von selbstndigen Agenturen.',1,'',1177082857);
