INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (102,20,'{FORUM_JAVASCRIPT_NOTIFICATION}\r\n<div id=\"forum\">\r\n<div style=\"text-align:right;\">{FORUM_COMMUNITY_LINKS}</div><br />\r\n<span style=\"color: rgb(255, 0, 0);\">{TXT_FORUM_ERROR}&nbsp;</span><span style=\"color: #006900;\">{TXT_FORUM_SUCCESS}&nbsp;</span><br /><br />\r\n\r\n<!-- BEGIN notification -->\r\n<form id=\"frmForumNotification\" action=\"?section=forum&amp;cmd=notification\" method=\"post\" onsubmit=\"SelectAllList(document.getElementById(\'frmForumNotification\').elements[\'subscribed[]\']);\">\r\n<div class=\"div_title\">\r\n {TXT_FORUM_THREAD_NOTIFICATION}\r\n</div>\r\n<table width=\"100%\" border=\"0\">\r\n  <tr> \r\n    <td width=\"13%\">\r\n      Verfgbare Foren/Kategorien<br />\r\n      <select name=\"unsubscribed[]\" size=\"32\" style=\"width:225px;\" multiple=\"multiple\">\r\n          {FORUM_NOTIFICATION_UNSUBSCRIBED}\r\n      </select>\r\n      <br />\r\n      <a href=\"javascript:SelectAllList(document.getElementById(\'frmForumNotification\').elements[\'unsubscribed[]\']);\" style=\"color:blue;\">{TXT_SELECT_ALL}<br />\r\n      </a><a href=\"javascript:DeselectAllList(document.getElementById(\'frmForumNotification\').elements[\'unsubscribed[]\']);\" style=\"color:blue;\">{TXT_DESELECT_ALL}</a>\r\n    </td>\r\n    <td style=\"vertical-align:middle;\" width=\"4%\"> \r\n      <div> \r\n        <input type=\"button\" value=\"&gt;&gt;\" name=\"adduser\" onclick=\"AddToTheList(document.getElementById(\'frmForumNotification\').elements[\'unsubscribed[]\'],document.getElementById(\'frmForumNotification\').elements[\'subscribed[]\'],adduser,removeuser);\" />\r\n        <br />\r\n        <input type=\"button\" value=\"&lt;&lt;\" name=\"removeuser\" onclick=\"RemoveFromTheList(document.getElementById(\'frmForumNotification\').elements[\'unsubscribed[]\'],document.getElementById(\'frmForumNotification\').elements[\'subscribed[]\'],adduser,removeuser);\" />\r\n      </div>\r\n    </td>\r\n    <td width=\"1%\"> \r\n      <div align=\"left\"> \r\n        Abonnierte Foren/Kategorien<br />\r\n        <select name=\"subscribed[]\" size=\"32\" style=\"width:225px;\" multiple=\"multiple\">\r\n		{FORUM_NOTIFICATION_SUBSCRIBED}\r\n        </select>\r\n        <br />\r\n        <a href=\"javascript:SelectAllList(document.getElementById(\'frmForumNotification\').elements[\'subscribed[]\']);\" style=\"color:blue;\">{TXT_SELECT_ALL}<br />\r\n        </a><a href=\"javascript:DeselectAllList(document.getElementById(\'frmForumNotification\').elements[\'subscribed[]\']);\" style=\"color:blue;\">{TXT_DESELECT_ALL}</a>\r\n      </div>\r\n    </td>\r\n    <td width=\"82%\">&nbsp;</td>\r\n  </tr>\r\n</table>\r\n<br />\r\n<div style=\"float: right;\"> <a href=\"?section=forum\">zum Forum</a> </div>\r\n<input type=\"submit\" value=\"{TXT_FORUM_NOTIFICATION_SUBMIT}\" name=\"forumNotificationSubmit\" />\r\n</form>\r\n<!-- END notification -->\r\n</div>\r\n<br />\r\n{TXT_FORUM_NOTIFICATION_HELPTEXT}','Abonnierte Foren/Kategorien','notification','y',97,'on','kon',30,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (100,20,'{FORUM_JAVASCRIPT_GOTO} {FORUM_JAVASCRIPT_DELETE} {FORUM_JAVASCRIPT_SCROLLTO} {FORUM_JAVASCRIPT_INSERT_TEXT}\r\n<div id=\"forum\">\r\n<div style=\"float: right;\">{FORUM_COMMUNITY_LINKS}</div><br />\r\n<span style=\"color: rgb(255, 0, 0);\">{TXT_FORUM_ERROR}&nbsp;</span><span style=\"color: #006900;\">{TXT_FORUM_SUCCESS}&nbsp;</span><br />\r\n<!-- BEGIN addPostAnchor -->\r\n<div style=\"float:left;\"><a href=\"#post\"><img border=\"0\" style=\"vertical-align:top; padding-right: 3px;\" src=\"images/modules/forum/reply.gif\" title=\"{TXT_FORUM_CREATE_POST}\" alt=\"{TXT_FORUM_CREATE_POST}\" />{TXT_FORUM_CREATE_POST}</a> </div>\r\n<!-- END addPostAnchor -->\r\n<div style=\"text-align: right;\">{FORUM_DROPDOWN}</div>\r\n<div class=\"div_title\">{FORUM_TREE}{FORUM_NAME}</div>\r\n<div class=\"div_board\">\r\n<table cellspacing=\"0\" cellpadding=\"0\" border=\"0\">\r\n	<tbody>\r\n		<!-- BEGIN forumPosts -->\r\n		<tr class=\"row_{FORUM_POST_ROWCLASS}\">\r\n			<td><a name=\"p{FORUM_POST_ID}\" id=\"p{FORUM_POST_ID}\" />\r\n				<!--{FORUM_USER_IMAGE}-->\r\n				<img border=\"0\" src=\"images/modules/forum/user.gif\" title=\"usericon\" alt=\"usericon\" />\r\n				<span style=\"vertical-align:top;\">{FORUM_USER_NAME}, {FORUM_POST_DATE}</span>\r\n			</td>\r\n			<td style=\"text-align: right;\">\r\n				<span class=\"rating\"> {TXT_FORUM_RATING}:\r\n					{FORUM_POST_RATING}\r\n				</span>\r\n				<!-- BEGIN rating -->\r\n				<span id=\"forum_rating_{FORUM_RATING_POST_ID}\">\r\n					<a title=\"+1\" href=\"javascript:void(0);\" onclick=\"ratePost({FORUM_RATING_POST_ID}, 1);\" >\r\n					[\r\n					<span class=\"rate\">\r\n						+\r\n					</span>\r\n					]\r\n					</a>\r\n					<a title=\"-1\" href=\"javascript:void(0);\" onclick=\"ratePost({FORUM_RATING_POST_ID}, -1);\">\r\n					[\r\n					<span class=\"rate\">\r\n						-\r\n					</span>\r\n					]\r\n					</a>\r\n				</span>\r\n			<!-- END rating -->\r\n			&nbsp;&nbsp; {FORUM_POST_NUMBER}\r\n			</td>\r\n		</tr>\r\n		<tr class=\"row_{FORUM_POST_ROWCLASS}\">\r\n			<td colspan=\"2\"><hr style=\"padding:0px;margin:0px;\" /></td>\r\n		</tr>\r\n		<!--<tr class=\"row_{FORUM_POST_ROWCLASS}\">\r\n			<td>		 		{FORUM_USER_GROUP}		 		{FORUM_USER_RANK}   								 	</td>\r\n			<td style=\"float: right;\">		 		{FORUM_USER_REGISTERED_SINCE}<br />\r\n			{FORUM_USER_POSTING_COUNT}<br />\r\n			{FORUM_USER_CONTACTS}		 	</td>\r\n		</tr>-->\r\n		<tr class=\"row_{FORUM_POST_ROWCLASS}\">\r\n			<td colspan=\"2\">\r\n				{FORUM_POST_ICON} <span style=\"vertical-align:top;\"> {FORUM_POST_SUBJECT}</span><br />\r\n			</td>\r\n		</tr>\r\n		<tr class=\"row_{FORUM_POST_ROWCLASS}\">\r\n			<td colspan=\"2\">\r\n				{FORUM_POST_MESSAGE}\r\n				<hr />\r\n			</td>\r\n		</tr>\r\n        <!-- BEGIN attachment -->\r\n		<tr class=\"row_{FORUM_POST_ROWCLASS}\">\r\n			<td colspan=\"2\">\r\n				{TXT_FORUM_FILE_ATTACHMENT}:\r\n				<a href=\"{FORUM_POST_ATTACHMENT_LINK}\">\r\n					<img alt=\"{FORUM_POST_ATTACHMENT_FILENAME}\" src=\"{FORUM_POST_ATTACHMENT_ICON}\" />\r\n					{FORUM_POST_ATTACHMENT_FILENAME} ({FORUM_POST_ATTACHMENT_FILESIZE} Bytes)\r\n				</a>\r\n			</td>\r\n		</tr>\r\n        <!-- END attachment -->\r\n		<tr class=\"row_{FORUM_POST_ROWCLASS}\">\r\n			<td class=\"bottomborder\">\r\n				<span class=\"last_edited\">{FORUM_POST_LAST_EDITED}</span> &nbsp;\r\n			</td>\r\n			<td class=\"bottomborder\" style=\"text-align: right;\">\r\n				<div style=\"margin-bottom: 3px;\">\r\n					<!--<input type=\"button\" value=\"{TXT_FORUM_EDIT}\" /><input type=\"button\" value=\"{TXT_FORUM_QUOTE}\" />-->\r\n					<!-- BEGIN postEdit -->\r\n					<a style=\"border: 1px solid black; padding: 2px;\" href=\"?section=forum&amp;cmd=thread&amp;id={FORUM_THREAD_ID}&amp;act=edit&amp;postid={FORUM_POST_ID}\">\r\n						{TXT_FORUM_EDIT}\r\n					</a>&nbsp;\r\n					<!-- END postEdit -->\r\n					<!-- BEGIN postQuote -->\r\n					<a style=\"border: 1px solid black; padding: 2px;\" href=\"?section=forum&amp;cmd=thread&amp;id={FORUM_THREAD_ID}&amp;act=quote&amp;postid={FORUM_POST_ID}\">\r\n						{TXT_FORUM_QUOTE}\r\n					</a>&nbsp;\r\n					<!-- END postQuote -->\r\n					<!-- BEGIN postDelete -->\r\n						<a style=\"border: 1px solid black; padding: 2px;\" onclick=\"deletePost(\'{FORUM_THREAD_ID}\', \'{FORUM_POST_ID}\'); return false;\" href=\"?section=forum&amp;cmd=thread&amp;id={FORUM_THREAD_ID}&amp;act=delete&amp;postid={FORUM_POST_ID}\">\r\n						{TXT_FORUM_DELETE}\r\n						</a>&nbsp;\r\n					<!-- END postDelete --><br />\r\n				</div>\r\n			</td>\r\n		</tr>\r\n		<!-- END forumPosts -->\r\n	</tbody>\r\n</table>\r\n</div>\r\n<br />\r\n{FORUM_POSTS_PAGING}\r\n<br /><br />\r\n<!-- BEGIN addPost -->\r\n<div class=\"div_title\" style=\"text-align: left;\"> 	{TXT_FORUM_ADD_POST} </div>\r\n<form enctype=\"multipart/form-data\" name=\"frm_thread\" action=\"?section=forum&amp;cmd=thread&amp;id={FORUM_THREAD_ID}\" method=\"post\" onsubmit=\"setScrollPos();\">\r\n	<div class=\"div_board\">\r\n	<table cellspacing=\"0\" cellpadding=\"0\" class=\"bottomborder\">\r\n		<!--<tr>\r\n				<th colspan=\"2\" style=\"text-align: left;\">\r\n					{TXT_FORUM_ADD_POST}\r\n				</th>\r\n			</tr>-->\r\n		<tbody>\r\n			<tr>\r\n				<td><a name=\"post\" /><!--{TXT_FORUM_ICON}--></td>\r\n				<td nowrap=\"nowrap\" colspan=\"2\">\r\n					<ul>\r\n					<!-- BEGIN icons -->\r\n					<li style=\"display: none;\">\r\n						<input type=\"radio\" style=\"border: 0px none ;\" id=\"icons_{FORUM_ICON_VALUE}\" name=\"icons\" value=\"{FORUM_ICON_VALUE}\" />\r\n						<label for=\"icons_{FORUM_ICON_VALUE}\">\r\n							<img src=\"{FORUM_ICON_SRC}\" alt=\"{FORUM_ICON_ALT}\" title=\"{FORUM_ICON_TITLE}\" />\r\n						</label>\r\n					</li>\r\n					<!-- END icons -->\r\n					</ul>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					{TXT_FORUM_SUBJECT}\r\n				</td>\r\n				<td colspan=\"2\">\r\n					<input class=\"inputtext\" type=\"text\" name=\"subject\" value=\"{FORUM_SUBJECT}\" />\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					{TXT_FORUM_KEYWORDS}\r\n				</td>\r\n				<td colspan=\"2\">\r\n					<input onfocus=\"showToolTip(\'{TXT_FORUM_COMMA_SEPARATED_KEYWORDS}\', this, \'forumKeywordsHelp\')\" onblur=\"hideToolTip(\'forumKeywordsHelp\')\" title=\"{TXT_FORUM_COMMA_SEPARATED_KEYWORDS}\" class=\"inputtext\" type=\"text\" name=\"keywords\" value=\"{FORUM_KEYWORDS}\" />\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					{TXT_FORUM_MESSAGE}\r\n				</td>\r\n				<td colspan=\"2\">\r\n					{FORUM_MESSAGE_INPUT}\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					{TXT_FORUM_FILE_ATTACHMENT}\r\n				</td>\r\n				<td colspan=\"2\">\r\n					<input class=\"inputtext\" type=\"file\" value=\"{FORUM_ATTACHMENT_VALUE}\" name=\"forum_attachment\" />\r\n					<!-- BEGIN delAttachment -->\r\n					    <br /><input type=\"checkbox\" id=\"delAttachment\" name=\"forum_delete_attachment\" value=\"1\" />\r\n						<label for=\"delAttachment\">{TXT_FORUM_DELETE_ATTACHMENT}</label>\r\n						<input type=\"hidden\" name=\"forum_attachment_oldname\" value=\"{FORUM_ATTACHMENT_OLDNAME}\" />\r\n					<!-- END delAttachment -->\r\n				</td>\r\n				</tr>\r\n				<!-- BEGIN captcha -->\r\n				<tr>\r\n					<td style=\"vertical-align: middle;\"> 	Captcha-Code:  </td>\r\n					<td style=\"vertical-align: middle; width: 10%;\"> 					<img src=\"{FORUM_CAPTCHA_IMAGE_URL}\" title=\"{FORUM_CAPTCHA_IMAGE_ALT}\" alt=\"{FORUM_CAPTCHA_IMAGE_ALT}\" />	 				</td>\r\n					<td style=\"vertical-align: middle; width: 90%;\"> 					<input type=\"text\" name=\"captcha\" size=\"6\" /> 					<br /></td>\r\n				</tr>\r\n				<!-- END captcha -->\r\n\r\n				<!-- BEGIN notificationRow -->\r\n				<tr>\r\n				  <td>\r\n					&nbsp;\r\n				  </td>\r\n				  <td colspan=\"2\">\r\n					<input id=\"forum_notify\" type=\"checkbox\" name=\"notification\" value=\"notify\" {FORUM_NOTIFICATION_CHECKBOX_CHECKED} /> <label for=\"forum_notify\">{TXT_FORUM_NOTIFY_NEW_POSTS}</label><br /><br />\r\n					 <input type=\"submit\" name=\"notification_update\" value=\"{TXT_FORUM_UPDATE_NOTIFICATION}\" /><br />\r\n				  </td>\r\n				</tr>\r\n			  <!-- END notificationRow -->\r\n			</tbody>\r\n		</table>\r\n\r\n		<br />\r\n		<input type=\"reset\" value=\"{TXT_FORUM_RESET}\" />\r\n		<!-- BEGIN createPost -->\r\n			<input type=\"submit\" name=\"create\" value=\"{TXT_FORUM_CREATE_POST}\" />\r\n		<!-- END createPost -->\r\n		<!-- BEGIN updatePost -->\r\n			<input type=\"submit\" name=\"update\" value=\"{TXT_FORUM_UPDATE_POST}\" />\r\n		<!-- END updatePost -->\r\n		<!-- BEGIN previewNewPost -->\r\n			<input type=\"submit\" name=\"preview_new\" value=\"{TXT_FORUM_PREVIEW}\" />\r\n		<!-- END previewNewPost -->\r\n		<!-- BEGIN previewEditPost -->\r\n			<input type=\"submit\" name=\"preview_edit\" value=\"{TXT_FORUM_PREVIEW}\" />\r\n		<!-- END previewEditPost -->\r\n	</div>\r\n		<input type=\"hidden\" name=\"post_edit_user_id\" value=\"{FORUM_POST_EDIT_USERID}\" />\r\n		<input type=\"hidden\" name=\"post_id\" value=\"{FORUM_EDIT_POST_ID}\" />\r\n		<input type=\"hidden\" name=\"thread_id\" value=\"{FORUM_THREAD_ID}\" />\r\n		<input type=\"hidden\" name=\"category_id\" value=\"{FORUM_CATEGORY_ID}\" />\r\n		<input type=\"hidden\" name=\"pos\" value=\"{FORUM_PAGING_POS}\" />\r\n		<input type=\"hidden\" name=\"scrollpos\" id=\"scrollpos\" />\r\n		<input type=\"hidden\" name=\"offset\" value=\"{FORUM_CAPTCHA_OFFSET}\" />\r\n	</form>\r\n	<!-- END addPost -->\r\n</div><!-- END div#forum -->\r\n\r\n<script type=\"text/javascript\" language=\"JavaScript\">\r\n//<![CDATA[\r\n  window.scrollTo(0, {FORUM_SCROLLPOS});\r\n//]]>\r\n</script>\r\n\r\n','Thread anzeigen','thread','y',97,'off','kon',20,'1');
