INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (507,4,'<!-- BEGIN newsletter_error_message -->\r\n<div style=\"color:#ff0000;\">{NEWSLETTER_ERROR_MESSAGE}</div>\r\n<!-- END newsletter_error_message -->\r\n<!-- BEGIN newsletter_ok_message -->\r\n<div>{NEWSLETTER_OK_MESSAGE}</div>\r\n<!-- END newsletter_ok_message -->\r\n<!-- BEGIN newsletterForm -->\r\n<form name=\"newsletter\" action=\"?section=newsletter&amp;cmd=profile&amp;code={NEWSLETTER_USER_CODE}&amp;mail={NEWSLETTER_PROFILE_MAIL}\" method=\"post\">\r\n	<table width=\"100%\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\r\n		<tbody style=\"vertical-align:top;\">\r\n			<tr>\r\n				<td width=\"15%\"><b>{TXT_NEWSLETTER_EMAIL_ADDRESS}</b></td>\r\n				<td width=\"85%\"><input type=\"text\" name=\"email\" size=\"40\" maxlength=\"255\" value=\"{NEWSLETTER_EMAIL}\" /></td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"15%\">{TXT_NEWSLETTER_SEX}</td>\r\n				<td width=\"85%\">\r\n					<input type=\"radio\" id=\"female\" name=\"sex\" value=\"f\" {NEWSLETTER_SEX_F} /> <label for=\"female\"> {TXT_NEWSLETTER_FEMALE} </label>\r\n					<input type=\"radio\" id=\"male\" name=\"sex\" value=\"m\" {NEWSLETTER_SEX_M} /> <label for=\"male\">{TXT_NEWSLETTER_MALE} </label>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"15%\">{TXT_NEWSLETTER_TITLE}</td>\r\n				<td width=\"85%\">{NEWSLETTER_TITLE}</td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"15%\">{TXT_NEWSLETTER_LASTNAME}</td>\r\n				<td width=\"85%\"><input type=\"text\" name=\"lastname\" size=\"40\" maxlength=\"255\" value=\"{NEWSLETTER_LASTNAME}\" /></td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"15%\">{TXT_NEWSLETTER_FIRSTNAME}</td>\r\n				<td width=\"85%\"><input type=\"text\" name=\"firstname\" size=\"40\" maxlength=\"255\" value=\"{NEWSLETTER_FIRSTNAME}\" /></td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"15%\">{TXT_NEWSLETTER_COMPANY}</td>\r\n				<td width=\"85%\"><input type=\"text\" name=\"company\" size=\"40\" maxlength=\"255\" value=\"{NEWSLETTER_COMPANY}\" /></td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"15%\">{TXT_NEWSLETTER_STREET}</td>\r\n				<td width=\"85%\"><input type=\"text\" name=\"street\" size=\"40\" maxlength=\"255\" value=\"{NEWSLETTER_STREET}\" /></td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"15%\">{TXT_NEWSLETTER_ZIP}</td>\r\n				<td width=\"85%\"><input type=\"text\" name=\"zip\" size=\"40\" maxlength=\"255\" value=\"{NEWSLETTER_ZIP}\" /></td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"15%\">{TXT_NEWSLETTER_CITY}</td>\r\n				<td width=\"85%\"><input type=\"text\" name=\"city\" size=\"40\" maxlength=\"255\" value=\"{NEWSLETTER_CITY}\" /></td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"15%\">{TXT_NEWSLETTER_COUNTRY}</td>\r\n				<td width=\"85%\"><input type=\"text\" name=\"country\" size=\"40\" maxlength=\"255\" value=\"{NEWSLETTER_COUNTRY}\" /></td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"15%\">{TXT_NEWSLETTER_PHONE}</td>\r\n				<td width=\"85%\"><input type=\"text\" name=\"phone\" size=\"40\" maxlength=\"255\" value=\"{NEWSLETTER_PHONE}\" /></td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"15%\">{TXT_NEWSLETTER_BIRTHDAY}</td>\r\n				<td width=\"85%\">\r\n					<select  name=\"day\">\r\n						<!-- BEGIN birthday_day -->\r\n							<option value=\"{USERS_BIRTHDAY_DAY}\" {SELECTED_DAY}> {USERS_BIRTHDAY_DAY_NAME} </option>\r\n						<!-- END birthday_day -->\r\n					</select>\r\n					<select  name=\"month\">\r\n						<!-- BEGIN birthday_month -->\r\n							<option value=\"{USERS_BIRTHDAY_MONTH}\" {SELECTED_MONTH}> {USERS_BIRTHDAY_MONTH_NAME} </option>\r\n						<!-- END birthday_month -->\r\n					</select>\r\n					<select  name=\"year\">\r\n						<!-- BEGIN birthday_year -->\r\n							<option value=\"{USERS_BIRTHDAY_YEAR}\" {SELECTED_YEAR}> {USERS_BIRTHDAY_YEAR} </option>\r\n						<!-- END birthday_year -->\r\n					</select>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"15%\">&nbsp;</td>