INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (523,31,'<!-- START memberdir -->\r\n<div id=\"memberdir\"><!-- BEGIN tree --><a title=\"{TXT_OVERVIEW}\" href=\"index.php?section=memberdir\">{TXT_OVERVIEW}</a> <!-- BEGIN tree-element -->&gt; <a title=\"{MEMBERDIR_DIRNAME}\" href=\"index.php?section=memberdir&amp;id={MEMBERDIR_DIRID}\">{MEMBERDIR_DIRNAME}</a><!-- END tree-element --> <br /><br /><!-- END tree -->\r\n\r\n{MEMBERDIR_DESCRIPTION} \r\n\r\n<!-- BEGIN category_list --><script type=\"text/javascript\">\r\n/* <![CDATA[ */\r\n	function openSubDir(obj) {\r\n		var id = obj.id.substr(10);\r\n		document.getElementById(\"block_\" + id).style.display = \"block\";\r\n		obj.src = \"admin/images/icons/minuslink.gif\";\r\n		obj.onclick = function() {closeSubDir(this)};\r\n	}\r\n	\r\n	function closeSubDir(obj) {\r\n		var id = obj.id.substr(10);\r\n		document.getElementById(\"block_\" + id).style.display = \"none\";\r\n		obj.src = \"admin/images/icons/pluslink.gif\";\r\n		obj.onclick = function() {openSubDir(this)};\r\n	}\r\n/* ]]> */\r\n</script>\r\n{TXT_CATEGORY_TREE_DESC}\r\n<!-- BEGIN category -->\r\n	<!-- BEGIN div-block-beginning -->\r\n<div id=\"block_{MEMBERDIR_PARENT_ID}\" style=\"display: none; padding-left: {MEMBERDIR_PADDING_LEFT}px;\">\r\n<!-- END div-block-beginning -->\r\n<div class=\"directory\">\r\n<img id=\"memberdir_{MEMBERDIR_DIR_ID}\" style=\"border: none; width: 11px; height: 11px;\" onclick=\"openSubDir(this);\" alt=\"Open subdir\" src=\"admin/images/icons/{MEMBERDIR_IMAGE_SRC}\" /> \r\n<a title=\"{MEMBERDIR_DIR_NAME}\" href=\"index.php?section=memberdir&amp;id={MEMBERDIR_DIR_ID}\">{MEMBERDIR_DIR_NAME}</a>\r\n</div>\r\n<!-- BEGIN div-block-ending -->\r\n</div>\r\n<!-- END div-block-ending -->\r\n<!-- END category --><br />\r\n<!-- END category_list -->\r\n\r\n<!-- BEGIN category_show -->\r\n{MEMBERDIR_CHAR_LIST} <br /><br />\r\n<form action=\"index.php?section=memberdir\" method=\"get\">\r\n    <input type=\"hidden\" name=\"search\" value=\"search\" /> <input type=\"hidden\" name=\"section\" value=\"memberdir\" /> <input onclick=\"this.value=\'\'\" name=\"keyword\" value=\"{MEMBERDIR_KEYWORD}\" /> {MEMBERDIR_DROPDOWN} <input type=\"submit\" value=\"{MEMBERDIR_SEARCH}\" />\r\n</form>\r\n<br /><br />\r\n<table style=\"TABLE-LAYOUT: fixed; WIDTH: 100%\" cellspacing=\"0\">\r\n    <!-- BEGIN memberdir_row -->\r\n    <tbody>\r\n        <tr class=\"memberdir_row{MEMBERDIR_ROW}\">\r\n            <td><a title=\"{FIELD_NAME} {FIELD_VORNAME}\" href=\"?section=memberdir&amp;mid={MEMBERDIR_ID}\"><strong>{FIELD_NAME} {FIELD_VORNAME}</strong><br />{FIELD_FIRMA}<br />{FIELD_BESCHREIBUNG}</a></td>\r\n            <td style=\"VERTICAL-ALIGN: top\"><a title=\"{FIELD_NAME} {FIELD_VORNAME}\" href=\"?section=memberdir&amp;mid={MEMBERDIR_ID}\">{FIELD_CATEGORY}{FIELD_ADRESSE}<br />{FIELD_PLZ} {FIELD_ORT}<br /><br /></a></td>\r\n        </tr>\r\n        <!-- END memberdir_row -->\r\n    </tbody>\r\n</table>\r\n<br /><br />{MEMBERDIR_PAGING}\r\n\r\n <!-- END category_show -->\r\n\r\n<!-- BEGIN memberdir_detail_view -->\r\n{MEMBERDIR_PIC1} {MEMBERDIR_PIC2}\r\n<table cellspacing=\"0\" cellpadding=\"3\" width=\"100%\" border=\"0\">\r\n    <!-- BEGIN row -->\r\n    <tbody>\r\n        <tr class=\"row{MEMBERDIR_FIELD_ROW}\">\r\n            <td style=\"VERTICAL-ALIGN: top\" width=\"150\"><strong>{MEMBERDIR_FIELD_NAME}:</strong></td>\r\n            <td style=\"VERTICAL-ALIGN: top\">{MEMBERDIR_FIELD_VALUE}</td>\r\n        </tr>\r\n        <!-- END row -->\r\n    </tbody>\r\n</table>\r\n<br /><br /><a title=\"Zurck\" href=\"javascript:history.back();\">Zur&uuml;ck</a> <!-- END memberdir_detail_view --></div>\r\n<!-- END memberdir -->','Adressverzeichnis','','y',0,'off','system',5,'1');
