INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (529,10,'<strong><a title=\"Eintragen\" href=\"index.php?section=guestbook&amp;cmd=post\">Eintragen</a></strong><br />\r\n{GUESTBOOK_TOTAL_ENTRIES} Eintr&auml;ge im G&auml;stebuch.<br />\r\n{GUESTBOOK_PAGING}<br />\r\n{GUESTBOOK_STATUS} <br />\r\n<br />\r\n<table width=\"100%\" cellspacing=\"1\" cellpadding=\"1\" border=\"0\">\r\n    <!-- BEGIN guestbook_row -->\r\n    <tbody>\r\n        <tr class=\"{GUESTBOOK_ROWCLASS}\">\r\n            <td valign=\"top\"><img hspace=\"0\" border=\"0\" src=\"images/modules/guestbook/post.gif\" alt=\"\" /> <strong>{GUESTBOOK_NICK}</strong> {GUESTBOOK_GENDER} {GUESTBOOK_LOCATION}</td>\r\n            <td valign=\"top\" nowrap=\"nowrap\">\r\n            <div align=\"right\">{GUESTBOOK_DATE}</div>\r\n            </td>\r\n        </tr>\r\n        <tr class=\"{GUESTBOOK_ROWCLASS}\">\r\n            <td valign=\"top\" colspan=\"2\"><br />\r\n            {GUESTBOOK_COMMENT}</td>\r\n        </tr>\r\n        <tr class=\"{GUESTBOOK_ROWCLASS}\">\r\n            <td valign=\"top\">{GUESTBOOK_EMAIL} {GUESTBOOK_URL}</td>\r\n        </tr>\r\n        <tr>\r\n            <td colspan=\"2\"><hr size=\"1\" noshade=\"noshade\" />\r\n            </td>\r\n        </tr>\r\n        <!-- END guestbook_row -->\r\n    </tbody>\r\n</table>','Gstebuch','','n',0,'on','system',8,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (530,10,'{ERROR} <br />\r\n<br />\r\nSie k&ouml;nnen sich hier ins G&auml;stebuch eintragen. <br />\r\nDamit der Eintrag klappt, sollten mindestens alle mit einem <font color=\"red\">*</font>  markierten Felder ausgef&uuml;llt werden.  <br />\r\n<form name=\"GuestbookForm\" action=\"index.php?section=guestbook&amp;cmd=post\" method=\"post\">\r\n    <br />\r\n    <strong>Name:</strong><font color=\"red\"> *</font> <br />\r\n    <input style=\"width: 350px;\" maxlength=\"255\" size=\"60\" name=\"nickname\" id=\"nickname\" value=\"{NICKNAME}\" /> <br />\r\n    <br />\r\n    <strong>Kommentar:</strong><font color=\"red\"> *</font>  <br />\r\n    <textarea style=\"width: 350px;\" name=\"comment\" id=\"comment\" rows=\"6\" cols=\"60\">{COMMENT}</textarea><br />\r\n    <br />\r\n    <strong>Geschlecht: </strong><font color=\"red\">*</font> <br />\r\n    <input type=\"radio\" value=\"F\" name=\"malefemale\" /> Weiblich<br />\r\n    <input type=\"radio\" value=\"M\" name=\"malefemale\" /> M&auml;nnlich<br />\r\n    <br />\r\n    <strong>Wohnort:</strong> <font color=\"red\">*</font> <br />\r\n    <input style=\"width: 350px;\" maxlength=\"255\" size=\"60\" name=\"location\" id=\"location\" value=\"{LOCATION}\" /> <br />\r\n    <strong>E-mail:</strong>&nbsp;<font color=\"#ff0000\">*</font> <br />\r\n    <input style=\"width: 350px;\" maxlength=\"255\" size=\"60\" name=\"email\" id=\"email\" value=\"{EMAIL}\" /> <br />\r\n    <strong>Homepage:</strong> <br />\r\n    <input style=\"width: 350px;\" name=\"url\" value=\"{HOMEPAGE}\" size=\"60\" maxlength=\"255\" />  <br />\r\n    <br />\r\n    <img src=\"{IMAGE_URL}\" alt=\"{IMAGE_ALT}\" title=\"{IMAGE_ALT}\" /><br />\r\n    <br />\r\n    {TXT_CAPTCHA} - Um maschinelle und automatische &Uuml;bertragung von Spamkommentaren zu verhinden, bitte die Zeichenfolge im dargestellten Bild in der Eingabemaske eintragen. Nur wenn die Zeichenfolge richtig eingegeben wurde, kann der Kommentar angenommen werden.<br />\r\n    <input type=\"text\" style=\"width: 4em;\" name=\"captcha\" /> <br />\r\n    <br />\r\n    <input type=\"hidden\" name=\"offset\" value=\"{CAPTCHA_OFFSET}\" /> <input type=\"reset\" value=\"&nbsp;Reset&nbsp;\" name=\"Submit\" />&nbsp;&nbsp; <input type=\"submit\" value=\"&nbsp;Speichern&nbsp;\" name=\"save\" />\r\n</form>','Eintragen','post','n',529,'on','system',1,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (531,16,'{SHOP_JAVASCRIPT_CODE}  {SHOP_MENU} <br />\r\n{SHOP_CART_INFO}<br />\r\n<strong>{TXT_PRODUCTS_IN_CATEGORY} {SHOP_CATEGORY_NAME}</strong>  {SHOP_PRODUCT_PAGING}   <!-- BEGIN shopProductRow -->\r\n<form method=\"post\" action=\"index.php?section=shop&amp;cmd=cart\" name=\"{SHOP_PRODUCT_FORM_NAME}\" id=\"{SHOP_PRODUCT_FORM_NAME}\">\r\n    <input type=\"hidden\" value=\"{SHOP_PRODUCT_ID}\" name=\"productId\" />\r\n    <div class=\"shop_products\">\r\n    <div class=\"shop_title\"><strong>{SHOP_PRODUCT_TITLE}</strong></div>\r\n    <!-- BEGIN productImage1 -->\r\n    <div class=\"shop_image\">&nbsp;<a href=\"{SHOP_PRODUCT_THUMBNAIL_LINK_1}\"><img border=\"0\" src=\"{SHOP_PRODUCT_THUMBNAIL_1}\" alt=\"{TXT_SEE_LARGE_PICTURE}\" /></a></div>\r\n    <!-- END productImage1 -->        <!-- END productImage3 -->  <!-- BEGIN productImageLink1 --> <!-- END productImageLink1 -->\r\n    <div class=\"shop_description\">\r\n    <div style=\"display: none;\">{TXT_PRODUCT_ID}:{SHOP_PRODUCT_ID} ({TXT_SHOP_PRODUCT_CUSTOM_ID}: {SHOP_PRODUCT_CUSTOM_ID})</div>\r\n    {SHOP_PRODUCT_DESCRIPTION}<br />\r\n    <br />\r\n    <div align=\"left\">{SHOP_PRODUCT_DETAILLINK}</div>\r\n    </div>\r\n    <div class=\"shop_links\">\r\n    <div class=\"shop_options\">{SHOP_PRODUCT_STOCK}</div>\r\n    <br />\r\n    <div class=\"shop_options\">{SHOP_MANUFACTURER_LINK}</div>\r\n    <br />\r\n    <!-- BEGIN shopProductOptionsRow -->\r\n    <div class=\"shop_options\">+ {SHOP_PRODUCT_OPTIONS_TITLE}<br />\r\n    <div style=\"display: none;\" id=\"product_options_layer{SHOP_PRODUCT_ID}\">\r\n    <div class=\"shop_options_click\"><br />\r\n    <!-- BEGIN shopProductOptionsValuesRow -->      <strong>{SHOP_PRODUCT_OPTIONS_NAME}:</strong><br />\r\n    {SHOP_PRODCUT_OPTION}<br />\r\n    <!-- END shopProductOptionsValuesRow --></div>\r\n    </div>\r\n    </div>\r\n    <!-- END shopProductOptionsRow --></div>\r\n    <div class=\"shop_prices\"><strong>Preis: {SHOP_PRODUCT_DISCOUNTPRICE} {SHOP_PRODUCT_DISCOUNTPRICE_UNIT}  {SHOP_PRODUCT_PRICE} {SHOP_PRODUCT_PRICE_UNIT}</strong><br />\r\n    {SHOP_PRODUCT_TAX_PREFIX}&nbsp;{SHOP_PRODUCT_TAX}\r\n    <div style=\"margin: 5px 0pt 0pt;\">\r\n    <div align=\"right\" class=\"shop_add_to_card\"><input type=\"submit\" value=\"{TXT_ADD_TO_CARD}\" name=\"{SHOP_PRODUCT_SUBMIT_NAME}\" onclick=\"{SHOP_PRODUCT_SUBMIT_FUNCTION}\" /></div>\r\n    </div>\r\n    </div>\r\n    </div>\r\n</form>\r\n<!-- END shopProductRow -->\r\n<div align=\"center\" class=\"shop_paging\">{SHOP_PRODUCT_PAGING}</div>','Online Shop','','n',0,'on','system',9,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (532,16,'<!-- BEGIN shopProductRow1 -->\r\n<table width=\"100%\" border=\"0\">\r\n    <tbody>\r\n        <tr valign=\"top\">\r\n            <td border=\"0\"> <strong>{SHOP_PRODUCT_TITLE}</strong> <br />\r\n            <a href=\"{SHOP_PRODUCT_DETAILLINK}\">\r\n              <img border=\"0\" alt=\"{SHOP_PRODUCT_TITLE}\" src=\"{SHOP_PRODUCT_THUMBNAIL}\" />\r\n            </a> <br />\r\n            {TXT_PRICE_NOW} {SHOP_PRODUCT_DISCOUNTPRICE}\r\n            {SHOP_PRODUCT_DISCOUNTPRICE_UNIT}\r\n            {TXT_INSTEAD_OF}\r\n            {SHOP_PRODUCT_PRICE} {SHOP_PRODUCT_PRICE_UNIT} <br />\r\n            </td>\r\n            <!-- BEGIN shopProductRow2 -->\r\n            <td width=\"50%\" border=\"0\">\r\n              <strong>{SHOP_PRODUCT_TITLE}</strong><br />\r\n              <a href=\"{SHOP_PRODUCT_DETAILLINK}\">\r\n                <img border=\"0\" alt=\"{SHOP_PRODUCT_TITLE}\" src=\"{SHOP_PRODUCT_THUMBNAIL}\" />\r\n              </a><br />\r\n              {TXT_PRICE_NOW}\r\n              {SHOP_PRODUCT_DISCOUNTPRICE} {SHOP_PRODUCT_DISCOUNTPRICE_UNIT}\r\n              {TXT_INSTEAD_OF}\r\n              {SHOP_PRODUCT_PRICE} {SHOP_PRODUCT_PRICE_UNIT}\r\n            </td>\r\n            <!-- END shopProductRow2 -->\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<!-- END shopProductRow1 -->','Sonderangebote','discounts','y',531,'on','system',1,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (533,16,'<!-- BEGIN shopCart -->\r\n<form action =\"index.php?section=shop&amp;cmd=cart\" name=\"shopForm\" method =\"post\">\r\n  <table width=\"100%\" cellpadding=\"0\" cellspacing=\"0\" border=\"0\">\r\n    <tr valign=\"middle\">\r\n      <td align=\"center\">\r\n        <table width=\"100%\" border=\"0\" cellpadding=\"2\" cellspacing=\"1\">\r\n          <tr>\r\n            <td colspan=\"7\">\r\n              <hr width=\"100%\" noshade=\"noshade\" color=\"black\" size=\"1\" />\r\n            </td>\r\n          </tr>\r\n          <tr valign=\"top\">\r\n            <td width=\"10%\"><div align=\"left\"><b>{TXT_SHOP_PRODUCT_CUSTOM_ID}</b></div></td>\r\n            <td width=\"35%\"><div align=\"left\"><b>{TXT_PRODUCT}</b></div></td>\r\n            <td width=\"10%\"><div align=\"left\"><b>{TXT_UNIT_PRICE}</b></div></td>\r\n            <td width=\"5%\"><div align=\"left\"><b>{TXT_QUANTITY}</b></div></td>\r\n            <td width=\"10%\"><div align=\"left\"><b>{TXT_WEIGHT}</b></div></td>\r\n            <td width=\"10%\"><div align=\"left\"><b>{TXT_TAX_PREFIX}</b></div></td>\r\n            <td width=\"10%\"><div align=\"right\"><b>{TXT_TOTAL}</b></div></td>\r\n          </tr>\r\n          <tr>\r\n            <td colspan=\"7\" valign=\"top\">\r\n              <hr width=\"100%\" color=\"#cccccc\" noshade=\"noshade\" size=\"1\" />\r\n            </td>\r\n          </tr>\r\n          <!-- BEGIN shopCartRow -->\r\n          <tr valign=\"top\">\r\n            <td>{SHOP_PRODUCT_CUSTOM_ID}</td>\r\n            <td><a href =\"?section=shop&amp;cmd=details&amp;referer=cart&amp;productId={SHOP_PRODUCT_CART_ID}\">{SHOP_PRODUCT_TITLE}</a>{SHOP_PRODUCT_OPTIONS}</td>\r\n            <td>{SHOP_PRODUCT_ITEMPRICE}&nbsp;{SHOP_PRODUCT_ITEMPRICE_UNIT}</td>\r\n            <td><input class=\"form\" type=\"text\" name=\"quantity