INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (557,47,'{BLOG_SEARCH_JAVASCRIPT}\r\n<form action=\"?section=blog&amp;cmd=search\" name=\"frmDoSearch\" method=\"post\">\r\n    <table width=\"100%\" cellspacing=\"0\" cellpadding=\"2\" border=\"0\">\r\n        <tbody>\r\n            <tr>\r\n                <td width=\"20%\"><strong>{TXT_SEARCH_MODUS}:</strong></td>\r\n                <td width=\"1%\" nowrap=\"nowrap\"><input type=\"radio\" onclick=\"switchModus();\" value=\"Keyword\" id=\"searchModus_RadioKeyword\" name=\"frmAddComment_Modus\" />&nbsp;{TXT_SEARCH_MODUS_KEYWORD}</td>\r\n                <td width=\"79%\" nowrap=\"nowrap\"><input type=\"radio\" value=\"Date\" onclick=\"switchModus();\" id=\"searchModus_RadioDate\" name=\"frmAddComment_Modus\" />&nbsp;{TXT_SEARCH_MODUS_DATE}</td>\r\n            </tr>\r\n        </tbody>\r\n    </table>\r\n    <div style=\"\" id=\"searchModus_DivKeyword\">\r\n    <h2 class=\"blogTitle\">{TXT_SEARCH_MODUS_KEYWORD}</h2>\r\n    <br />\r\n    <input type=\"text\" style=\"width: 200px; padding-left: 5px;\" value=\"{BLOG_SEARCH_KEYWORD_STRING}\" name=\"frmDoSearch_Keyword_String\" /> {BLOG_SEARCH_KEYWORD_CATEGORIES} <input type=\"submit\" value=\"{TXT_SEARCH_SUBMIT}\" name=\"frmDoSearch_Submit\" /></div>\r\n    <div style=\"\" id=\"searchModus_DivDate\">\r\n    <h2 class=\"blogTitle\">{TXT_SEARCH_MODUS_DATE}</h2>\r\n    <br />\r\n    <div style=\"margin: auto; width: 200px;\">{BLOG_SEARCH_DATE_CALENDAR}</div>\r\n    </div>\r\n</form>\r\n<!-- BEGIN ResultPart -->\r\n<h2 class=\"blogTitle\">{TXT_SEARCH_RESULTS}</h2>\r\n<!-- BEGIN showResults -->\r\n<h3 class=\"blogSubtitle\"><a title=\"{BLOG_SEARCH_RESULTS_SUBJECT}\" href=\"?section=blog&amp;cmd=details&amp;id={BLOG_SEARCH_RESULTS_MID}\">{BLOG_SEARCH_RESULTS_SUBJECT}</a></h3>\r\n<div class=\"blogContent\">{BLOG_SEARCH_RESULTS_INTRODUCTION}<a href=\"?section=blo