INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (563,8,'<h3>So k&ouml;nnen Sie {NEWS_HOSTNAME} &uuml;ber einen RSS-Newsreader lesen</h3>\r\n<p>RSS ist ein elektronisches Nachrichtenformat, das dem Nutzer erm&ouml;glicht, die Inhalte einer Website &ndash; oder Teile davon &ndash; als sogenannte RSS-Feeds zu abonnieren oder in andere Websites zu integrieren.<br />\r\nDer Feed dieser Website kann z.B. &uuml;ber die folgende Adresse aufgerufen werden:<br />\r\n<a href=\"{NEWS_RSS_FEED_URL}\" title=\"RSS Newsfeed von {NEWS_HOSTNAME}\" class=\"rssfeed\">{NEWS_RSS_FEED_URL}</a></p>\r\n<h3><br />\r\nOnline Einbindung</h3>\r\nDer Feed dieser Website kann auch ganz einfach auf Ihrer Website dargestellt werden. Dazu m&uuml;ssen Sie lediglich den folgenden Code in Ihre eigene Webseite einf&uuml;gen:<br />\r\n<br />\r\n<form action=\"\">\r\n    <textarea style=\"width: 98%; font-size: 95%;\" rows=\"18\" cols=\"30\" name=\"code\">{NEWS_RSS2JS_CODE}</textarea>     <br />\r\n    <br />\r\n    <input type=\"button\" value=\"Alles markieren\" onclick=\"javascript:this.form.code.focus();this.form.code.select();\" name=\"button\" />\r\n</form>\r\n<br />\r\n<h3>Ausgabe</h3>\r\nGem&auml;ss obigem Beispiel wird die Darstellung des Feeds dann folgendermassen aussehen:<br />\r\n<br />\r\n<script language=\"JavaScript\" type=\"text/javascript\">\r\n<!--\r\n// Diese Variablen sind optional\r\nvar rssFeedFontColor = \"#000000\"; // Schriftfarbe\r\nvar rssFeedFontSize = 8; // Schriftgrsse\r\nvar rssFeedFont = \"Verdana, Arial\"; // Schriftart\r\nvar rssFeedLimit = 10; // Anzahl anzuzeigende Newsmeldungen\r\nvar rssFeedShowDate = true; // Datum der Newsmeldung anzeigen\r\nvar rssFeedTarget = \"_blank\"; // _blank | _parent | _self | _top\r\n// -->\r\n</script> <script type=\"text/javascript\" language=\"JavaScript\" src=\"{NEWS_RSS2JS_URL}\"></script> <noscript> &amp;amp;amp;amp;amp;amp;amp;lt;a href=&amp;amp;amp;amp;amp;amp;amp;quot;{NEWS_RSS_FEED_URL}&amp;amp;amp;amp;amp;amp;amp;quot;&amp;amp;amp;amp;amp;amp;amp;gt;{NEWS_HOSTNAME} - News anzeigen&amp;amp;amp;amp;amp;amp;amp;lt;/a&amp;amp;amp;amp;amp;amp;amp;gt; </noscript>','News Feed','feed','n',561,'on','system',2,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (564,8,'<b>{NEWS_TEASER_TEXT}</b><br />\r\n<br />\r\n{NEWS_TEXT}<br />\r\n<br />\r\n{NEWS_SOURCE}<br />\r\n{NEWS_URL}<br />\r\nVer&ouml;ffentlicht am:<br />\r\n{NEWS_DATE} <em>von {NEWS_AUTHOR}</em><br />\r\n<br />\r\n<div id=\"newsbox\">\r\n<div>\r\n<table width=\"129\" height=\"87\" cellspacing=\"1\" cellpadding=\"1\" border=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <td><img width=\"16\" height=\"16\" src=\"images/content/icons/printer.png\" alt=\"Print\" /></td>\r\n            <td><a href=\"{PRINT_URL}\">Drucken</a></td>\r\n        </tr>\r\n        <tr>\r\n            <td><img width=\"16\" height=\"16\" src=\"images/content/icons/page_white_acrobat.png\" alt=\"Acrobat\" /></td>\r\n            <td><a href=\"{PDF_URL}\">PDF</a></td>\r\n        </tr>\r\n        <tr>\r\n            <td><img width=\"16\" height=\"16\" src=\"images/content/icons/arrow_right.png\" alt=\"Arrow Right\" /></td>\r\n            <td><a href=\"index.php?section=recommend\">Weiterempfehlen</a></td>\r\n        </tr>\r\n        <tr>\r\n            <td><img width=\"16\" height=\"16\" src=\"images/content/icons/rss.png\" alt=\"RSS\" /></td>\r\n            <td><a href=\"feed/news_headlines_de.xml\">RSS Abonnieren</a></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n</div>','Newsmeldung','details','n',561,'off','system',3,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (565,8,'<font color=\"#ff0000\">{NEWS_STATUS_MESSAGE}&nbsp;</font> <!-- BEGIN news_submit_form -->\r\n<form action=\"index.php?section=news&amp;cmd=submit\" method=\"post\">\r\n    <h2>{TXT_NEWS_MESSAGE}&nbsp;</h2>\r\n    <table width=\"100%\" cellspacing=\"0\" cellpadding=\"2\" border=\"0\">\r\n        <tbody>\r\n            <tr>\r\n                <td width=\"20%\">{TXT_TITLE}</td>\r\n                <td width=\"80%\"><input type=\"text\" style=\"width: 250px;\" name=\"newsTitle\" value=\"{NEWS_TITLE}\" maxlength=\"250\" /></td>\r\n            </tr>\r\n            <tr>\r\n                <td width=\"20%\">{TXT_CATEGORY}</td>\r\n                <td width=\"80%\"><select style=\"width: 250px;\" name=\"newsCat\"><!-- BEGIN news_category_menu -->\r\n                <option value=\"{NEWS_CATEGORY_ID}\">{NEWS_CATEGORY_TITLE}</option>\r\n                <!-- END news_category_menu --></select></td>\r\n            </tr>\r\n            <tr>\r\n                <td width=\"20%\">{TXT_EXTERNAL_SOURCE}</td>\r\n                <td width=\"80%\"><input type=\"text\" style=\"width: 250px;\" name=\"newsSource\" value=\"{NEWS_SOURCE}\" maxlength=\"250\" /></td>\r\n            </tr>\r\n            <tr>\r\n                <td width=\"20%\">{TXT_LINK} #1</td>\r\n                <td width=\"80%\"><input type=\"text\" style=\"width: 250px;\" name=\"newsUrl1\" value=\"{NEWS_URL1}\" maxlength=\"250\" /></td>\r\n            </tr>\r\n            <tr>\r\n                <td width=\"20%\">{TXT_LINK} #2</td>\r\n                <td width=\"80%\"><input type=\"text\" style=\"width: 250px;\" name=\"newsUrl2\" value=\"{NEWS_URL2}\" maxlength=\"250\" /></td>\r\n            </tr>\r\n        </tbody>\r\n    </table>\r\n    <br />\r\n    <h2>{TXT_NEWS_TEASER_TEXT}&nbsp;</h2>\r\n    <table width=\"100%\" cellspacing=\"0\" cellpadding=\