INSERT INTO `contrexx_content` (`id`, `lang_id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `useContentFromLang`, `expertmode`) VALUES (660,1,'{CONTACT_FEEDBACK_TEXT}\n<!-- BEGIN formText -->Dies ist ein Besipielformular.<br /><!-- END formText --><br /><br />\n<div id=\"contactFormError\" style=\"color: red; display: none;\">\n{TXT_NEW_ENTRY_ERORR}\n</div>\n<br />\n<!-- BEGIN contact_form -->\n<fieldset id=\"contactFrame\">\n<legend>Kontaktformular</legend>\n<form action=\"index.php?section=contact&amp;cmd=4\" \nmethod=\"post\" enctype=\"multipart/form-data\" onsubmit=\"return checkAllFields();\" id=\"contactForm\" class=\"contactForm\">\n<p> <label for=\"contactFormFieldId_26\">Anrede </label>\n<select class=\"contactFormClass_select\" name=\"contactFormField_26\" id=\"contactFormFieldId_26\">\n<option>Frau</option>\n<option>Herr</option>\n</select>\n</p>\n<p> <label for=\"contactFormFieldId_27\">Vorname <strong class=\"is_required\">*</strong></label>\n<input class=\"contactFormClass_text\" id=\"contactFormFieldId_27\" type=\"text\" name=\"contactFormField_27\" value=\"{27_VALUE}\" />\n</p>\n<p> <label for=\"contactFormFieldId_28\">Nachname <strong class=\"is_required\">*</strong></label>\n<input class=\"contactFormClass_text\" id=\"contactFormFieldId_28\" type=\"text\" name=\"contactFormField_28\" value=\"{28_VALUE}\" />\n</p>\n<p> <label for=\"contactFormFieldId_29\">Firma </label>\n<input class=\"contactFormClass_text\" id=\"contactFormFieldId_29\" type=\"text\" name=\"contactFormField_29\" value=\"{29_VALUE}\" />\n</p>\n<p> <label for=\"contactFormFieldId_30\">Strasse / Nr. </label>\n<input class=\"contactFormClass_text\" id=\"contactFormFieldId_30\" type=\"text\" name=\"contactFormField_30\" value=\"{30_VALUE}\" />\n</p>\n<p> <label for=\"contactFormFieldId_31\">PLZ / Ort </label>\n<input class=\"contactFormClass_text\" id=\"contactFormFieldId_31\" type=\"text\" name=\"contactFormField_31\" value=\"{31_VALUE}\" />\n</p>\n<p> <label for=\"contactFormFieldId_33\">Telefon </label>\n<input class=\"contactFormClass_text\" id=\"contactFormFieldId_33\" type=\"text\" name=\"contactFormField_33\" value=\"{33_VALUE}\" />\n</p>\n<p> <label for=\"contactFormFieldId_35\">E-Mail <strong class=\"is_required\">*</strong></label>\n<input class=\"contactFormClass_text\" id=\"contactFormFieldId_35\" type=\"text\" name=\"contactFormField_35\" value=\"{35_VALUE}\" />\n</p>\n<p> <label for=\"contactFormFieldId_39\">Bemerkungen </label>\n<textarea class=\"contactFormClass_textarea\" name=\"contactFormField_39\" id=\"contactFormFieldId_39\" rows=\"5\" cols=\"20\">{39_VALUE}</textarea>\n</p>\n<!-- BEGIN contact_form_captcha -->\n<div style=\"color: red;\">{CONTACT_CAPTCHA_ERROR}</div>\n<p>\n{TXT_CONTACT_CAPTCHA_DESCRIPTION}<br />\n</p>\n<p><span>CAPTCHA</span><img class=\"captcha\" src=\"{CONTACT_CAPTCHA_URL}\" alt=\"{CONTACT_CAPTCHA_ALT}\" />\n<input id=\"contactFormCaptcha\" type=\"text\" name=\"contactFormCaptcha\" /><br />\n<input type=\"hidden\" name=\"contactFormCaptchaOffset\" value=\"{CONTACT_CAPTCHA_OFFSET}\" />\n</p>\n<!-- END contact_form_captcha -->\n<p>\n<input class=\"contactFormClass_button\" type=\"submit\" name=\"submitContactForm\" value=\"{TXT_CONTACT_SUBMIT}\" /><input class=\"contactFormClass_button\" type=\"reset\" value=\"{TXT_CONTACT_RESET}\" />\n</p>\n</form>\n</fieldset>\n<!-- END contact_form -->\n<script src=\"lib/datepickercontrol/datepickercontrol.js\" type=\"text/javascript\"></script>\n<script type=\"text/javascript\">\n/* <![CDATA[ */\nfields = new Array();\nfields[26] = Array(\n	\'Anrede\',\n	0,\n	\'.*\',\n	\'select\');\nfields[27] = Array(\n	\'Vorname\',\n	1,\n	\'.*\',\n	\'text\');\nfields[28] = Array(\n	\'Nachname\',\n	1,\n	\'.*\',\n	\'text\');\nfields[29] = Array(\n	\'Firma\',\n	0,\n	\'.*\',\n	\'text\');\nfields[30] = Array(\n	\'Strasse / Nr.\',\n	0,\n	\'.*\',\n	\'text\');\nfields[31] = Array(\n	\'PLZ / Ort\',\n	0,\n	\'.*\',\n	\'text\');\nfields[33] = Array(\n	\'Telefon\',\n	0,\n	\'.*\',\n	\'text\');\nfields[35] = Array(\n	\'E-Mail\',\n	1,\n	\'^[_a-zA-Z0-9-]+(\\\\.[_a-zA-Z0-9-]+)*@[a-zA-Z0-9-]+(\\\\.[a-zA-Z0-9-]+)*\\\\.(([0-9]{1,3})|([a-zA-Z]{2,3})|(aero|coop|info|museum|name))$\',\n	\'text\');\nfields[39] = Array(\n	\'Bemerkungen\',\n	0,\n	\'.*\',\n	\'textarea\');\nfunction checkAllFields() {\n    var isOk = true;\n\n    for (var field in fields) {\n        var type = fields[field][3];\n        if (type == \'text\' || type == \'file\' || type == \'password\' || type == \'textarea\') {\n            value = document.getElementsByName(\'contactFormField_\' + field)[0].value;\n            if (value == \"\" && isRequiredNorm(fields[field][1], value)) {\n                isOk = false;\n                document.getElementsByName(\'contactFormField_\' + field)[0].style.border = \"red 1px solid\";\n            } else if (value != \"\" && !matchType(fields[field][2], value)) {\n                isOk = false;\n                document.getElementsByName(\'contactFormField_\' + field)[0].style.border = \"red 1px solid\";\n            } else {\n                document.getElementsByName(\'contactFormField_\' + field)[0].style.borderColor = \'\';\n            }\n        } else if (type == \'checkbox\') {\n            if (!isRequiredCheckbox(fields[field][1], field)) {\n                isOk = false;\n            }\n        } else if (type == \'checkboxGroup\') {\n            if (!isRequiredCheckBoxGroup(fields[field][1], field)) {\n                isOk = false;\n            }\n        } else if (type == \'radio\') {\n            if (!isRequiredRadio(fields[field][1], field)) {\n                isOk = false;\n            }\n        }\n    }\n\n    if (!isOk) {\n        document.getElementById(\'contactFormError\').style.display = \"block\";\n    }\n    return isOk;\n}\nfunction isRequiredNorm(required, value) {\n    if (required == 1) {\n        if (value == \"\") {\n            return true;\n        }\n    }\n    return false;\n}\nfunction matchType(pattern, value) {\n    var reg = new RegExp(pattern);\n    if (value.match(reg)) {\n        return true;\n    }\n    return false;\n}\nfunction isRequiredCheckbox(required, field) {\n    if (required == 1) {\n        if (!document.getElementsByName(\'contactFormField_\' + field)[0].checked) {\n            document.getElementsByName(\'contactFormField_\' + field)[0].style.border = \"red 1px solid\";\n            return false;\n        }\n    }\n    document.getElementsByName(\'contactFormField_\' + field)[0].style.borderColor = \'\';\n\n    return true;\n}\nfunction isRequiredCheckBoxGroup(required, field) {\n    if (required == true) {\n        var boxes = document.getElementsByName(\'contactFormField_\' + field + \'[]\');\n        var checked = false;\n        for (var i = 0; i < boxes.length; i++) {\n            if (boxes[i].checked) {\n                checked = true;\n            }\n        }\n        if (checked) {\n            setListBorder(\'contactFormField_\' + field + \'[]\', false);\n            return true;\n        } else {\n            setListBorder(\'contactFormField_\' + field + \'[]\', \'1px red solid\');\n            return false;\n        }\n    } else {\n        return true;\n    }\n}\nfunction isRequiredRadio(required, field) {\n    if (required == 1) {\n        var buttons = document.getElementsByName(\'contactFormField_\' + field);\n        var checked = false;\n        for (var i = 0; i < buttons.length; i++) {\n            if (buttons[i].checked) {\n                checked = true;\n            }\n        }\n        if (checked) {\n            setListBorder(\'contactFormField_\' + field, false);\n            return true;\n        } else {\n            setListBorder(\'contactFormField_\' + field, \'1px red solid\');\n            return false;\n        }\n    } else {\n        return true;\n    }\n}\nfunction setListBorder(field, borderColor) {\n    var boxes = document.getElementsByName(field);\n    for (var i = 0; i < boxes.length; i++) {\n        if (borderColor) {\n            boxes[i].style.border = borderColor;\n        } else {\n            boxes[i].style.borderColor = \'\';\n        }\n    }\n}\n/* ]]> */\n</script>\n','Kontakt Formular','Kontakt Formular','Kontakt Formular','Kontakt Formular','index','','',0,'n');
