INSERT INTO `contrexx_content` (`id`, `lang_id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `useContentFromLang`, `expertmode`) VALUES (1149,1,'Das Internet als Vertriebskanal gewinnt zunehmen an Bedeutung und wird in Zukunft noch wichtiger werden. Daher ist es schon heute wichtig, dass Unternehmen schon heute ein entsprechendes Angebot und Produkteinformationen im Internet aufbauen und aktualisieren.<br />\r\n<br />\r\nIn Studien geben rund die H&auml;lfte der Befragten an, dass meistens dort einkaufen wo sie die entsprechenden Produkteinformationen gefunden haben. - Nutzen Sie die Chance und setzen Sie das Internet gezielt als Marketinginstrument ein.<br />','Marktplatz: Internet als Verkaufskanal','Marktplatz: Internet als Verkaufskanal','Das Internet als Vertriebskanal gewinnt zunehmen an Bedeutung und wird in Zukunft noch wichtiger werden.','Marktplatz, Online-Shop, Kleinanzeigen, Inserate, Onlinedesk, Job-Auschreibung','index','','',0,'n');
INSERT INTO `contrexx_content` (`id`, `lang_id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `useContentFromLang`, `expertmode`) VALUES (1150,1,'Unterhaltende Elemente helfen die Internetseite weiter zu verbreiten und neue Besucher erneut auf die Webseite zu locken. Diese Elemente eignen sich um Emotionen zu wecken und Sympathie aufzubauen.<br />\r\n<br />\r\nSuchen Sie sich einen Weg, sich von ihren direkten Wettbewerbern abzuheben und das Vertrauen der Internetbenutzer zu gewinnen.<br />\r\n<br />\r\n<div style=\"text-align: center;\"><object height=\"344\" width=\"425\">\r\n<param value=\"http://www.youtube.com/v/N2utFVvAY6Q&amp;hl=de&amp;fs=1\" name=\"movie\" />\r\n<param value=\"true\" name=\"allowFullScreen\" />\r\n<param value=\"always\" name=\"allowscriptaccess\" /><embed height=\"344\" width=\"425\" allowfullscreen=\"true\" allowscriptaccess=\"always\" type=\"application/x-shockwave-flash\" src=\"http://www.youtube.com/v/N2utFVvAY6Q&amp;hl=de&amp;fs=1\"></embed></object></div>\r\n<br />\r\n<br />','Unterhaltung','Unterhaltung','Unterhaltung','Unterhaltung','index','','',0,'n');
