on matchType(pattern, value) {\r\n		var reg = new RegExp(pattern);\r\n		if (value.match(reg)) {\r\n			return true;\r\n		}\r\n		return false;\r\n	} \r\n	\r\n	-->\r\n	</script>\r\n<div id=\"auction\">\r\n	<form name=\"formEntry\" enctype=\"multipart/form-data\" method=\"post\" action=\"?section=auction&cmd=confirm\" onsubmit=\"return CheckForm()\">\r\n	<input type=\"hidden\" name=\"id\" value=\"{AUCTION_ENTRY_ID}\" >\r\n		<table width=\"100%\" cellspacing=\"0\" cellpadding=\"2\" border=\"0\" align=\"top\" class=\"adminlist\">\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_AUCTION_TITLE_ENTRY}</td>		\r\n				<td><input type=\"text\" name=\"title\" style=\"width: 300px;\" maxlength=\"100\" value=\"\" ></td>		\r\n			</tr>\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_AUCTION_CATEGORIE}</td>		\r\n				<td><select name=\"cat\"  style=\"width: 305px;\">{AUCTION_CATEGORIES}</select></td>\r\n			</td>\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_AUCTION_DESCRIPTION}</td>\r\n				<td><textarea name=\"description\" rows=\"5\"  style=\"width: 300px; overflow: auto;\"></textarea></td>\r\n			</tr>\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_AUCTION_PICTURE} 1</td>		\r\n				<td>{ENTRY_PICTURE}<input type=\"file\" name=\"pic_1\" size=\"38\" style=\"width: 304px;\" maxlength=\"100\"></td>\r\n			</td>	\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_AUCTION_PICTURE} 2</td>		\r\n				<td>{ENTRY_PICTURE}<input type=\"file\" name=\"pic_2\" size=\"38\" style=\"width: 304px;\" maxlength=\"100\"></td>\r\n			</td>	\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_AUCTION_PICTURE} 3</td>		\r\n				<td>{ENTRY_PICTURE}<input type=\"file\" name=\"pic_3\" size=\"38\" style=\"width: 304px;\" maxlength=\"100\"></td>\r\n			</td>	\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_AUCTION_PICTURE} 4</td>		\r\n				<td>{ENT