ng verschickt','Mitteilung verschickt','index','','',0,'y');
INSERT INTO `contrexx_content` (`id`, `lang_id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `useContentFromLang`, `expertmode`) VALUES (1233,1,'<div id=\"blog\">\r\n	<div class=\"categories\">\r\n		<h2>Themen Kategorien</h2>\r\n		{BLOG_CATEGORIES_LIST}\r\n	</div>\r\n	<!-- BEGIN showBlogEntries -->\r\n		<div class=\"entries\">\r\n			<h2><a title=\"{BLOG_ENTRIES_TITLE}\" href=\"index.php?section=blog&amp;cmd=details&amp;id={BLOG_ENTRIES_ID}\">{BLOG_ENTRIES_TITLE}</a></h2>\r\n			<div class=\"posted\">\r\n				{BLOG_ENTRIES_POSTED_ICON}&nbsp;{BLOG_ENTRIES_POSTED}\r\n				<!-- BEGIN showCommentPart -->\r\n					&nbsp;&nbsp;|&nbsp;&nbsp;<a title=\"{TXT_BLOG_COMMENTS}\" href=\"index.php?section=blog&amp;cmd=details&amp;id={BLOG_ENTRIES_ID}#comments\">{BLOG_ENTRIES_COMMENTS}</a>\r\n				<!-- END showCommentPart -->\r\n                                <br />{BLOG_ENTRIES_TAGS_ICON}&nbsp;{BLOG_ENTRIES_TAGS}\r\n			</div>\r\n			<div class=\"content\">\r\n				<div class=\"image\">{BLOG_ENTRIES_IMAGE}</div>\r\n				<div class=\"text\">{BLOG_ENTRIES_INTRODUCTION}</div>\r\n				<div class=\"link\"><a href=\"index.php?section=blog&amp;cmd=details&amp;id={BLOG_ENTRIES_ID}\">Weiter...</a></div>\r\n			</div>\r\n			<div class=\"statistics\">\r\n				<strong>{TXT_BLOG_CATEGORIES}:</strong>&nbsp;{BLOG_ENTRIES_CATEGORIES}<br />\r\n				<!-- BEGIN showVotingPart -->\r\n					<strong>{TXT_BLOG_VOTING}:</strong>&nbsp; <a title=\"{TXT_BLOG_VOTING_DO}\" href=\"index.php?section=blog&amp;cmd=details&amp;id={BLOG_ENTRIES_ID}#voting\">{BLOG_ENTRIES_VOTING_STARS}</a>&nbsp;(<a title=\"{TXT_BLOG_VOTING_DO}\" href=\"index.php?section=blog&amp;cmd=details&amp;id={BLOG_ENTRIES_ID}#voting\">{BLOG_ENTRIES_VOTING}</a>) \r\n				<!-- END showVotingPart -->\r\n			</div>\r\n		</div>\r\n	<!-- END showBlogEntries -->\r\n	{BLOG_ENTRIES_PAGING}\r\n</div>','Blog','Blog','Blog','Blog','index','','',0,'y');
