INSERT INTO `contrexx_content` (`id`, `lang_id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `useContentFromLang`, `expertmode`) VALUES (1279,1,'{CONTACT_FEEDBACK_TEXT}\n<!-- BEGIN formText --><br /><!-- END formText --><br /><br />\n<div id=\"contactFormError\" style=\"color: red; display: none;\">\n{TXT_NEW_ENTRY_ERORR}\n</div>\n<br />\n<!-- BEGIN contact_form -->\n<fieldset id=\"contactFrame\">\n<legend>Bewerbungs Formular</legend>\n<form action=\"index.php?section=contact&amp;cmd=5\" \nmethod=\"post\" enctype=\"multipart/form-data\" onsubmit=\"return checkAllFields();\" id=\"contactForm\" class=\"contactForm\">\n<p> <label for=\"contactFormFieldId_43\">Stelle </label>\n<input class=\"contactFormClass_text\" id=\"contactFormFieldId_43\" type=\"text\" name=\"contactFormField_43\" value=\"{43_VALUE}\" />\n</p>\n<p> <label for=\"contactFormFieldId_40\">Vorname </label>\n<input class=\"contactFormClass_text\" id=\"contactFormFieldId_40\" type=\"text\" name=\"contactFormField_40\" value=\"{40_VALUE}\" />\n</p>\n<p> <label for=\"contactFormFieldId_41\">Nachname </label>\n<input class=\"contactFormClass_text\" id=\"contactFormFieldId_41\" type=\"text\" name=\"contactFormField_41\" value=\"{41_VALUE}\" />\n</p>\n<p> <label for=\"contactFormFieldId_42\">E-Mail </label>\n<input class=\"contactFormClass_text\" id=\"contactFormFieldId_42\" type=\"text\" name=\"contactFormField_42\" value=\"{42_VALUE}\" />\n</p>\n<p> <label for=\"contactFormFieldId_45\">Bewerbungsunterlagen </label>\n<input class=\"contactFormClass_file\" id=\"contactFormFieldId_45\" type=\"file\" name=\"contactFormField_45\" />\n</p>\n<p> <label for=\"contactFormFieldId_46\">Kommentar </label>\n<textarea class=\"contactFormClass_textarea\" name=\"contactFormField_46\" id=\"contactFormFieldId_46\" rows=\"5\" cols=\"20\">{46_VALUE}</textarea>\n</p>\n<p> <label for=\"contactFormFieldId_44\">&nbsp; </label>\n<input class=\"contactFormClass_hidden\" id=\"contactFormFieldId_44\" type=\"hidden\" name=\"contactFormField_44\" value=\"{44_VALUE}\" />\n</p>\n<!-- BEGIN contact_form_captcha -->\n<div style=\"color: red;\">{CONTACT_CAPTCHA_ERROR}</div>\n<p>\n{TXT_CONTACT_CAPTCHA_DESCRIPTION}<br />\n</p>\n<p><span>CAPTCHA</span><img class=\"captcha\" src=\"{CONTACT_CAPTCHA_URL}\" alt=\"{CONTACT_CAPTCHA_ALT}\" />\n<input id=\"contactFormCaptcha\" type=\"text\" name=\"contactFormCaptcha\" /><br />\n<input type=\"hidden\" name=\"contactFormCaptchaOffset\" value=\"{CONTACT_CAPTCHA_OFFSET}\" />\n</p>\n<!-- END contact_form_captcha -->\n<p>\n<input class=\"contactFormClass_button\" type=\"submit\" name=\"submitContactForm\" value=\"{TXT_CONTACT_SUBMIT}\" /><input class=\"contactFormClass_button\" type=\"reset\" value=\"{TXT_CONTACT_RESET}\" />\n</p>\n</form>\n</fieldset>\n<!-- END contact_form -->\n<script src=\"lib/datepickercontrol/datepickercontrol.js\" type=\"text/javascript\"></script>\n<script type=\"text/javascript\">\n/* <![CDATA[ */\nfields = new Array();\nfields[43] = Array(\n	\'Stelle\',\n	0,\n	\'.*\',\n	\'text\');\nfields[40] = Array(\n	\'Vorname\',\n	0,\n	\'.*\',\n	\'text\');\nfields[41] = Array(\n	\'Nachname\',\n	0,\n	\'.*\',\n	\'text\');\nfields[42] = Array(\n	\'E-Mail\',\n	0,\n	\'.*\',\n	\'text\');\nfields[45] = Array(\n	\'Bewerbungsunterlagen\',\n	0,\n	\'.*\',\n	\'file\');\nfields[46] = Array(\n	\'Kommentar\',\n	0,\n	\'.*\',\n	\'textarea\');\nfields[44] = Array(\n	\'Link\',\n	0,\n	\'.*\',\n	\'hidden\');\nfunction checkAllFields() {\n    var isOk = true;\n\n    for (var field in fields) {\n        var type = fields[field][3];\n        if (type == \'text\' || type == \'file\' || type == \'password\' || type == \'textarea\') {\n            value = document.getElementsByName(\'contactFormField_\' + field)[0].value;\n            if (value == \"\" && isRequiredNorm(fields[field][1], value)) {\n                isOk = false;\n                document.getElementsByName(\'contactFormField_\' + field)[0].style.border = \"red 1px solid\";\n            } else if (value != \"\" && !matchType(fields[field][2], value)) {\n                isOk = false;\n                document.getElementsByName(\'contactFormField_\' + field)[0].style.border = \"red 1px solid\";\n            } else {\n                document.getElementsByName(\'contactFormField_\' + field)[0].style.borderColor = \'\';\n            }\n        } else if (type == \'checkbox\') {\n            if (!isRequiredCheckbox(fields[field][1], field)) {\n                isOk = false;\n            }\n        } else if (type == \'checkboxGroup\') {\n            if (!isRequiredCheckBoxGroup(fields[field][1], field)) {\n                isOk = false;\n            }\n        } else if (type == \'radio\') {\n            if (!isRequiredRadio(fields[field][1], field)) {\n                isOk = false;\n            }\n        }\n    }\n\n    if (!isOk) {\n        document.getElementById(\'contactFormError\').style.display = \"block\";\n    }\n    return isOk;\n}\nfunction isRequiredNorm(required, value) {\n    if (required == 1) {\n        if (value == \"\") {\n            return true;\n        }\n    }\n    return false;\n}\nfunction matchType(pattern, value) {\n    var reg = new RegExp(pattern);\n    if (value.match(reg)) {\n        return true;\n    }\n    return false;\n}\nfunction isRequiredCheckbox(required, field) {\n    if (required == 1) {\n        if (!document.getElementsByName(\'contactFormField_\' + field)[0].checked) {\n            document.getElementsByName(\'contactFormField_\' + field)[0].style.border = \"red 1px solid\";\n            return false;\n        }\n    }\n    document.getElementsByName(\'contactFormField_\' + field)[0].style.borderColor = \'\';\n\n    return true;\n}\nfunction isRequiredCheckBoxGroup(required, field) {\n    if (required == true) {\n        var boxes = document.getElementsByName(\'contactFormField_\' + field + \'[]\');\n        var checked = false;\n        for (var i = 0; i < boxes.length; i++) {\n            if (boxes[i].checked) {\n                checked = true;\n            }\n        }\n        if (checked) {\n            setListBorder(\'contactFormField_\' + field + \'[]\', false);\n            return true;\n        } else {\n            setListBorder(\'contactFormField_\' + field + \'[]\', \'1px red solid\');\n            return false;\n        }\n    } else {\n        return true;\n    }\n}\nfunction isRequiredRadio(required, field) {\n    if (required == 1) {\n        var buttons = document.getElementsByName(\'contactFormField_\' + field);\n        var checked = false;\n        for (var i = 0; i < buttons.length; i++) {\n            if (buttons[i].checked) {\n                checked = true;\n            }\n        }\n        if (checked) {\n            setListBorder(\'contactFormField_\' + field, false);\n            return true;\n        } else {\n            setListBorder(\'contactFormField_\' + field, \'1px red solid\');\n            return false;\n        }\n    } else {\n        return true;\n    }\n}\nfunction setListBorder(field, borderColor) {\n    var boxes = document.getElementsByName(field);\n    for (var i = 0; i < boxes.length; i++) {\n        if (borderColor) {\n            boxes[i].style.border = borderColor;\n        } else {\n            boxes[i].style.borderColor = \'\';\n        }\n    }\n}\n/* ]]> */\n</script>\n','Bewerbungs Formular','Bewerbungs Formular','Bewerbungs Formular','Bewerbungs Formular','index','','',0,'n');
