INSERT INTO `contrexx_content` (`id`, `lang_id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `useContentFromLang`, `expertmode`) VALUES (1284,1,'If you need assistance in the usage of Contrexx or if you have any questions please don\'t hesitate to look up one of the following sections:<br />\r\n<ul>\r\n    <li><a href=\"http://www.contrexx.com/wiki/en/\" class=\"out\">Documentation</a>: The wiki based documentation portal for Contrexx users and developers.</li>\r\n    <li><a href=\"http://www.contrexx.com/forum/forumdisplay.php?f=69\" class=\"out\">Contrexx - Forum</a>: Further support is provided by the Contrexx community in the open source forum.</li>\r\n    <li><a href=\"http://www.contrexx.com/forum/forumdisplay.php?f=36\" class=\"out\">Bugtracker within the forum (in German)</a>: Maintained list of current known bugs as well as planned features and improvements for Contrexx.</li>\r\n    <li><a href=\"http://support.comvation.com/index.php\" class=\"out\">Support - Ticket for Contrexx - Customers</a>: Contrexx - Customers have to possibility to open a support-ticket. Tickets will be answered quickly and it\'s support is reliable. Users of the free opensource version can use one of the three other methods above.</li>\r\n</ul>','Contrexx Support','Contrexx Support','Contrexx Support','Contrexx Support','index','','',0,'n');
