INSERT INTO `contrexx_content` (`id`, `lang_id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `useContentFromLang`, `expertmode`) VALUES (1362,1,'<h3>Online Integration using the RSS-Newsfeed</h3>\r\n<p><a class=\"rssfeed\" title=\"RSS Newsfeed of {NEWS_HOSTNAME}\" href=\"{NEWS_RSS_FEED_URL}\" style=\"\"><img width=\"48\" height=\"74\" border=\"0\" alt=\"The feed of the website can be requested using this link\" src=\"images/content/icons/rss_bg.gif\" style=\"margin: 5px 20px 20px; float: right;\" /></a>RSS is an electronic news format which allows users to receive updates from a website without the need of visiting the page at all.</p>\r\n<h3>Online Integration using Javascript</h3>\r\nThe feed of this website can also be displayed in your website in a very simple way. Simple put the following code into your website:<br />\r\n<br />\r\n<form action=\"\">\r\n    <textarea name=\"code\" cols=\"30\" rows=\"18\" style=\"width: 98%; font-size: 95%;\">{NEWS_RSS2JS_CODE}</textarea>     <br />\r\n    <br />\r\n    <input type=\"button\" name=\"button\" onclick=\"javascript:this.form.code.focus();this.form.code.select();\" value=\"Select all\" />\r\n</form>','News Feed','News Feed','News Feed','News Feed','index','','',0,'n');
INSERT INTO `contrexx_content` (`id`, `lang_id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `useContentFromLang`, `expertmode`) VALUES (1420,1,'{CONTACT_FEEDBACK_TEXT} <!-- BEGIN formText --><br />\r\n<!-- END formText --><br />\r\n<br />\r\n<div style=\"color: red; display: none;\" id=\"contactFormError\">{TXT_NEW_ENTRY_ERORR}</div>\r\n<br />\r\n<!-- BEGIN contact_form --> <fieldset id=\"contactFrame\"> <legend>Apply for a job</legend>\r\n<form class=\"contactForm\" id=\"contactForm\" onsubmit=\"return checkAllFields();\" enctype=\"multipart/form-data\" method=\"post\" action=\"index.php?section=contact&amp;cmd=6\">\r\n    <p><label for=\"contactFormFieldId_48\">Job title </label> <input type=\"text\" value=\"{48_VALUE}\" name=\"contactFormField_48\" id=\"contactFormFieldId_48\" class=\"contactFormClass_text\" /></p>\r\n    <p><label for=\"contactFormFieldId_49\">Forename </label> <input type=\"text\" value=\"{49_VALUE}\" name=\"contactFormField_49\" id=\"contactFormFieldId_49\" class=\"contactFormClass_text\" /></p>\r\n    <p><label for=\"contactFormFieldId_50\">Surname </label> <input type=\"text\" value=\"{50_VALUE}\" name=\"contactFormField_50\" id=\"contactFormFieldId_50\" class=\"contactFormClass_text\" /></p>\r\n    <p><label for=\"contactFormFieldId_51\">E-Mail </label> <input type=\"text\" value=\"{51_VALUE}\" name=\"contactFormField_51\" id=\"contactFormFieldId_51\" class=\"contactFormClass_text\" /></p>\r\n    <p><label for=\"contactFormFieldId_52\">Curriculum Vitae </label> <input type=\"file\" name=\"contactFormField_52\" id=\"contactFormFieldId_52\" class=\"contactFormClass_file\" /></p>\r\n    <p><label for=\"contactFormFieldId_53\">Comment </label> <textarea cols=\"20\" rows=\"5\" id=\"contactFormFieldId_53\" name=\"contactFormField_53\" class=\"contactFormClass_textarea\">{53_VALUE}</textarea></p>\r\n    <p><label for=\"contactFormFieldId_54\">&nbsp; </label> <input type=\"hidden\" value=\"{54_VALUE}\" name=\"contactFormField_54\" id=\"contactFormFieldId_54\" class=\"contactFormClass_hidden\" /></p>\r\n    <!-- BEGIN contact_form_captcha -->\r\n    <div style=\"color: red;\">{CONTACT_CAPTCHA_ERROR}</div>\r\n    <p>{TXT_CONTACT_CAPTCHA_DESCRIPTION}</p>\r\n    <p><span>CAPTCHA</span><img alt=\"{CONTACT_CAPTCHA_ALT}\" src=\"{CONTACT_CAPTCHA_URL}\" class=\"captcha\" /> <input type=\"text\" name=\"contactFormCaptcha\" id=\"contactFormCaptcha\" /><br />\r\n    <input type=\"hidden\" value=\"{CONTACT_CAPTCHA_OFFSET}\" name=\"contactFormCaptchaOffset\" /></p>\r\n    <!-- END contact_form_captcha -->\r\n    <p><input type=\"submit\" value=\"{TXT_CONTACT_SUBMIT}\" name=\"submitContactForm\" class=\"contactFormClass_button\" /><input type=\"reset\" value=\"{TXT_CONTACT_RESET}\" class=\"contactFormClass_button\" /></p>\r\n</form>\r\n</fieldset> <!-- END contact_form --> <script src=\"lib/datepickercontrol/datepickercontrol.js\" type=\"text/javascript\"></script> <script type=\"text/javascript\">\r\n/* <![CDATA[ */\r\nfields = new Array();\r\nfields[48] = Array(\r\n	\'Job title\',\r\n	0,\r\n	\'.*\',\r\n	\'text\');\r\nfields[49] = Array(\r\n	\'Forename\',\r\n	0,\r\n	\'.*\',\r\n	\'text\');\r\nfields[50] = Array(\r\n	\'Surname\',\r\n	0,\r\n	\'.*\',\r\n	\'text\');\r\nfields[51] = Array(\r\n	\'E-Mail\',\r\n	0,\r\n	\'.*\',\r\n	\'text\');\r\nfields[52] = Array(\r\n	\'Curriculum Vitae\',\r\n	0,\r\n	\'.*\',\r\n	\'file\');\r\nfields[53] = Array(\r\n	\'Comment\',\r\n	0,\r\n	\'.*\',\r\n	\'textarea\');\r\nfields[54] = Array(\r\n	\'Link\',\r\n	0,\r\n	\'.*\',\r\n	\'hidden\');\r\nfunction checkAllFields() {\r\n    var isOk = true;\r\n\r\n    for (var field in fields) {\r\n        var type = fields[field][3];\r\n        if (type == \'text\' || type == \'file\' || type == \'password\' || type == \'textarea\') {\r\n            value = document.getElementsByName(\'contactFormField_\' + field)[0].value;\r\n            if (value == \"\" && isRequiredNorm(fields[field][1], value)) {\r\n                isOk = false;\r\n                document.getElementsByName(\'contactFormField_\' + field)[0].style.border = \"red 1px solid\";\r\n            } else if (value != \"\" && !matchType(fields[field][2], value)) {\r\n                isOk = false;\r\n                document.getElementsByName(\'contactFormField_\' + field)[0].style.border = \"red 1px solid\";\r\n            } else {\r\n                document.getElementsByName(\'contactFormField_\' + field)[0].style.borderColor = \'\';\r\n            }\r\n        } else if (type == \'checkbox\') {\r\n            if (!isRequiredCheckbox(fields[field][1], field)) {\r\n                isOk = false;\r\n            }\r\n        } else if (type == \'checkboxGroup\') {\r\n            if (!isRequiredCheckBoxGroup(fields[field][1], field)) {\r\n                isOk = false;\r\n            }\r\n        } else if (type == \'radio\') {\r\n            if (!isRequiredRadio(fields[field][1], field)) {\r\n                isOk = false;\r\n            }\r\n        }\r\n    }\r\n\r\n    if (!isOk) {\r\n        document.getElementById(\'contactFormError\').style.display = \"block\";\r\n    }\r\n    return isOk;\r\n}\r\nfunction isRequiredNorm(required, value) {\r\n    if (required == 1) {\r\n        if (value == \"\") {\r\n            return true;\r\n        }\r\n    }\r\n    return false;\r\n}\r\nfunction matchType(pattern, value) {\r\n    var reg = new RegExp(pattern);\r\n    if (value.match(reg)) {\r\n        return true;\r\n    }\r\n    return false;\r\n}\r\nfunction isRequiredCheckbox(required, field) {\r\n    if (required == 1) {\r\n        if (!document.getElementsByName(\'contactFormField_\' + field)[0].checked) {\r\n            document.getElementsByName(\'contactFormField_\' + field)[0].style.border = \"red 1px solid\";\r\n            return false;\r\n        }\r\n    }\r\n    document.getElementsByName(\'contactFormField_\' + field)[0].style.borderColor = \'\';\r\n\r\n    return true;\r\n}\r\nfunction isRequiredCheckBoxGroup(required, field) {\r\n    if (required == true) {\r\n        var boxes = document.getElementsByName(\'contactFormField_\' + field + \'[]\');\r\n        var checked = false;\r\n        for (var i = 0; i < boxes.length; i++) {\r\n            if (boxes[i].checked) {\r\n                checked = true;\r\n            }\r\n        }\r\n        if (checked) {\r\n            setListBorder(\'contactFormField_\' + field + \'[]\', false);\r\n            return true;\r\n        } else {\r\n            setListBorder(\'contactFormField_\' + field + \'[]\', \'1px red solid\');\r\n            return false;\r\n        }\r\n    } else {\r\n        return true;\r\n    }\r\n}\r\nfunction isRequiredRadio(required, field) {\r\n    if (required == 1) {\r\n        var buttons = document.getElementsByName(\'contactFormField_\' + field);\r\n        var checked = false;\r\n        for (var i = 0; i < buttons.length; i++) {\r\n            if (buttons[i].checked) {\r\n                checked = true;\r\n            }\r\n        }\r\n        if (checked) {\r\n            setListBorder(\'contactFormField_\' + field, false);\r\n            return true;\r\n        } else {\r\n            setListBorder(\'contactFormField_\' + field, \'1px red solid\');\r\n            return false;\r\n        }\r\n    } else {\r\n        return true;\r\n    }\r\n}\r\nfunction setListBorder(field, borderColor) {\r\n    var boxes = document.getElementsByName(field);\r\n    for (var i = 0; i < boxes.length; i++) {\r\n        if (borderColor) {\r\n            boxes[i].style.border = borderColor;\r\n        } else {\r\n            boxes[i].style.borderColor = \'\';\r\n        }\r\n    }\r\n}\r\n/* ]]> */\r\n</script>','Apply for a job','Apply for a job','Apply for a job','Apply for a job','index','','',0,'n');
INSERT INTO `contrexx_content` (`id`, `lang_id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `useContentFromLang`, `expertmode`) VALUES (1363,1,'{NEWS_TEXT}<br />\r\n<table cellspacing=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <th colspan=\"2\">Toolbox</th>\r\n        </tr>\r\n        <tr class=\"row1\">\r\n            <td width=\"1%\"><img width=\"16\" height=\"16\" alt=\"Print\" src=\"images/content/icons/printer-1.gif\" /></td>\r\n            <td width=\"99%\"><a href=\"{PRINT_URL}\">Drucken</a></td>\r\n        </tr>\r\n        <tr class=\"row2\">\r\n            <td><img width=\"16\" height=\"16\" alt=\"Acrobat\" src=\"images/content/icons/page_white_acrobat.gif\" /></td>\r\n            <td><a href=\"{PDF_URL}\">PDF</a></td>\r\n        </tr>\r\n        <tr class=\"row1\">\r\n            <td><img width=\"16\" height=\"16\" alt=\"Arrow Right\" src=\"images/content/icons/arrow_right.gif\" /></td>\r\n            <td><a href=\"index.php?section=recommend\">Weiterempfehlen</a></td>\r\n        </tr>\r\n        <tr class=\"row2\">\r\n            <td><img width=\"16\" height=\"16\" alt=\"RSS\" src=\"images/content/icons/rss.gif\" /></td>\r\n            <td><a href=\"feed/news_headlines_de.xml\">RSS Abonnieren</a></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<br />\r\n<table cellspacing=\"0\" border=\"0\" class=\"news_meta\">\r\n    <tbody>\r\n        <tr>\r\n            <td width=\"33%\">Ver&ouml;ffentlicht<br />{NEWS_DATE}</td>\r\n            <td width=\"33%\">{NEWS_SOURCE}</td>\r\n            <td width=\"33%\">{NEWS_URL}</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n</div>','Newsmeldung','Newsmeldung','Newsmeldung','Newsmeldung','index','','',0,'y');
INSERT INTO `contrexx_content` (`id`, `lang_id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `useContentFromLang`, `expertmode`) VALUES (1364,1,'<script type=\"text/javascript\">\r\n<!--\r\nfunction toggle(target){\r\n    obj = document.getElementById(target);\r\n    obj.style.display = (obj.style.display==\'none\') ? \'inline\' : \'none\';  \r\n    if (obj.style.display==\'none\' && target == \'hiddenSearch\'){\r\n         document.getElementById(\'searchCheck\').value = \'norm\';\r\n    }else if(obj.style.display==\'inline\' && target == \'hiddenSearch\'){\r\n         document.getElementById(\'searchCheck\').value = \'exp\';\r\n    }\r\n}\r\n-->\r\n</script>\r\n<div id=\"auction\">\r\n	<div class=\"search\">\r\n		<h1>&nbsp;{TXT_AUCTION_SEARCH_TITLE}</h1>\r\n		<form action=\"index.php?\" method=\"get\" name=\"auctionSearch\" id=\"auctionSearch\">\r\n			<input name=\"term\" value=\"{TXT_AUCTION_SEARCHTERM}\" size=\"25\" maxlength=\"100\" /><input type=\"hidden\" id=\"searchCheck\" name=\"check\" value=\"norm\" size=\"10\" /><input type=\"hidden\" name=\"section\" value=\"auction\" size=\"10\" /><input type=\"hidden\" name=\"cmd\" value=\"search\" size=\"10\" />&nbsp;<input type=\"submit\" value=\"{TXT_AUCTION_SEARCH}\" name=\"search\" />\r\n		</form>\r\n	</div>\r\n	<br />\r\n	\r\n	<div class=\"navtree\"><a href=\"?section=auction\">{TXT_AUCTION_AUCTIONS}</a>{AUCTION_CATEGORY_NAVI}</div>\r\n	\r\n	<br />\r\n	\r\n	\r\n	\r\n	<!-- BEGIN showCategories -->\r\n		<div class=\"categories\">\r\n			<h1>&nbsp;{TXT_AUCTION_CATEGORIES}</h1>\r\n			<div style=\"float: left; width: {AUCTION_CATEGORY_ROW_WIDTH};\">{AUCTION_CATEGORY_ROW1}</div>\r\n			<div style=\"float: left; width: {AUCTION_CATEGORY_ROW_WIDTH};\">{AUCTION_CATEGORY_ROW2}</div>\r\n		</div>\r\n	<!-- END showCategories -->   \r\n	\r\n	\r\n	<!-- BEGIN showEntriesHeader -->\r\n		<div class=\"entries\">\r\n		\r\n		<!-- BEGIN showCategoriesTitle -->\r\n			<h1>Kategorie:&nbsp