INSERT INTO `contrexx_content` (`id`, `lang_id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `useContentFromLang`, `expertmode`) VALUES (1386,1,'<div id=\"feed\">\r\n	{FEED_NO_NEWSFEED}\r\n	<!-- BEGIN feed_table -->\r\n	<!-- BEGIN feed_cat -->\r\n	<div class=\"categories\">\r\n		<table cellspacing=\"0\">\r\n			<tr>\r\n				<th>{FEED_CAT_NAME}</th>\r\n			</tr>\r\n			<!-- BEGIN feed_news -->\r\n			<tr>\r\n				<td><a href=\"{FEED_NEWS_LINK}\">{FEED_NEWS_NAME}</a></td>\r\n			</tr>\r\n			 <!-- END feed_news -->\r\n		</table>\r\n	</div>\r\n	<!-- END feed_cat -->\r\n	<br />\r\n	<!-- BEGIN feed_show_news -->\r\n	<div class=\"news\">\r\n    	<fieldset>\r\n            <legend>{FEED_TITLE}</legend>\r\n            {FEED_IMAGE}\r\n    		<ul>  \r\n    			<!-- BEGIN feed_output_news -->      \r\n    			<li><a href=\"{FEED_LINK}\" target=\"_blank\">{FEED_NAME}</a></li>     \r\n    			<!-- END feed_output_news --> \r\n    		</ul>\r\n    		<br />\r\n    		{TXT_FEED_LAST_UPTDATE}: {FEED_TIME}\r\n    	</fieldset>\r\n		<!-- END feed_show_news -->\r\n	</div>\r\n	<!-- END feed_table -->\r\n</div>','RSS Feed Directory','RSS Feed Directory','RSS Feed Directory','RSS Feed Directory','index','','',0,'y');
