INSERT INTO `contrexx_content` (`id`, `lang_id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `useContentFromLang`, `expertmode`) VALUES (1421,1,'{CONTACT_FEEDBACK_TEXT} <!-- BEGIN formText -->This is a sample contact form.<br />\r\n<!-- END formText --><br />\r\n<br />\r\n<div style=\"color: red; display: none;\" id=\"contactFormError\">{TXT_NEW_ENTRY_ERORR}</div>\r\n<br />\r\n<!-- BEGIN contact_form --> <fieldset id=\"contactFrame\"> <legend>Contact form</legend>\r\n<form class=\"contactForm\" id=\"contactForm\" onsubmit=\"return checkAllFields();\" enctype=\"multipart/form-data\" method=\"post\" action=\"index.php?section=contact&amp;cmd=7\">\r\n    <p><label for=\"contactFormFieldId_55\">Salutation </label> <select id=\"contactFormFieldId_55\" name=\"contactFormField_55\" class=\"contactFormClass_select\">\r\n    <option>Frau</option>\r\n    <option>Herr</option>\r\n    </select></p>\r\n    <p><label for=\"contactFormFieldId_56\">Forename <strong class=\"is_required\">*</strong></label> <input type=\"text\" value=\"{56_VALUE}\" name=\"contactFormField_56\" id=\"contactFormFieldId_56\" class=\"contactFormClass_text\" /></p>\r\n    <p><label for=\"contactFormFieldId_57\">Surname <strong class=\"is_required\">*</strong></label> <input type=\"text\" value=\"{57_VALUE}\" name=\"contactFormField_57\" id=\"contactFormFieldId_57\" class=\"contactFormClass_text\" /></p>\r\n    <p><label for=\"contactFormFieldId_58\">Company </label> <input type=\"text\" value=\"{58_VALUE}\" name=\"contactFormField_58\" id=\"contactFormFieldId_58\" class=\"contactFormClass_text\" /></p>\r\n    <p><label for=\"contactFormFieldId_59\">Street </label> <input type=\"text\" value=\"{59_VALUE}\" name=\"contactFormField_59\" id=\"contactFormFieldId_59\" class=\"contactFormClass_text\" /></p>\r\n    <p><label for=\"contactFormFieldId_60\">ZIP / City </label> <input type=\"text\" value=\"{60_VALUE}\" name=\"contactFormField_60\" id=\"contactFormFieldId_60\" class=\"contactFormClass_text\" /></p>\r\n    <p><label for=\"contactFormFieldId_61\">Telephone </label> <input type=\"text\" value=\"{61_VALUE}\" name=\"contactFormField_61\" id=\"contactFormFieldId_61\" class=\"contactFormClass_text\" /></p>\r\n    <p><label for=\"contactFormFieldId_62\">E-Mail <strong class=\"is_required\">*</strong></label> <input type=\"text\" value=\"{62_VALUE}\" name=\"contactFormField_62\" id=\"contactFormFieldId_62\" class=\"contactFormClass_text\" /></p>\r\n    <p><label for=\"contactFormFieldId_63\">Comments </label> <textarea cols=\"20\" rows=\"5\" id=\"contactFormFieldId_63\" name=\"contactFormField_63\" class=\"contactFormClass_textarea\">{63_VALUE}</textarea></p>\r\n    <!-- BEGIN contact_form_captcha -->\r\n    <div style=\"color: red;\">{CONTACT_CAPTCHA_ERROR}</div>\r\n    <p>{TXT_CONTACT_CAPTCHA_DESCRIPTION}</p>\r\n    <p><span>CAPTCHA</span><img alt=\"{CONTACT_CAPTCHA_ALT}\" src=\"{CONTACT_CAPTCHA_URL}\" class=\"captcha\" /> <input type=\"text\" name=\"contactFormCaptcha\" id=\"contactFormCaptcha\" /><br />\r\n    <input type=\"hidden\" value=\"{CONTACT_CAPTCHA_OFFSET}\" name=\"contactFormCaptchaOffset\" /></p>\r\n    <!-- END contact_form_captcha -->\r\n    <p><input type=\"submit\" value=\"{TXT_CONTACT_SUBMIT}\" name=\"submitContactForm\" class=\"contactFormClass_button\" /><input type=\"reset\" value=\"{TXT_CONTACT_RESET}\" class=\"contactFormClass_button\" /></p>\r\n</form>\r\n</fieldset> <!-- END contact_form --> <script src=\"lib/datepickercontrol/datepickercontrol.js\" type=\"text/javascript\"></script> <script type=\"text/javascript\">\r\n/* <![CDATA[ */\r\nfields = new Array();\r\nfields[55] = Array(\r\n	\'Salutation\',\r\n	0,\r\n	\'.*\',\r\n	\'select\');\r\nfields[56] = Array(\r\n	\'Forename\',\r\n	1,\r\n	\'.*\',\r\n	\'text\');\r\nfields[57] = Array(\r\n	\'Surname\',\r\n	1,\r\n	\'.*\',\r\n	\'text\');\r\nfields[58] = Array(\r\n	\'Company\',\r\n	0,\r\n	\'.*\',\r\n	\'text\');\r\nfields[59] = Array(\r\n	\'Street\',\r\n	0,\r\n	\'.*\',\r\n	\'text\');\r\nfields[60] = Array(\r\n	\'ZIP / City\',\r\n	0,\r\n	\'.*\',\r\n	\'text\');\r\nfields[61] = Array(\r\n	\'Telephone\',\r\n	0,\r\n	\'.*\',\r\n	\'text\');\r\nfields[62] = Array(\r\n	\'E-Mail\',\r\n	1,\r\n	\'^[_a-zA-Z0-9-]+(\\\\.[_a-zA-Z0-9-]+)*@[a-zA-Z0-9-]+(\\\\.[a-zA-Z0-9-]+)*\\\\.(([0-9]{1,3})|([a-zA-Z]{2,3})|(aero|coop|info|museum|name))$\',\r\n	\'text\');\r\nfields[63] = Array(\r\n	\'Comments\',\r\n	0,\r\n	\'.*\',\r\n	\'textarea\');\r\nfunction checkAllFields() {\r\n    var isOk = true;\r\n\r\n    for (var field in fields) {\r\n        var type = fields[field][3];\r\n        if (type == \'text\' || type == \'file\' || type == \'password\' || type == \'textarea\') {\r\n            value = document.getElementsByName(\'contactFormField_\' + field)[0].value;\r\n            if (value == \"\" && isRequiredNorm(fields[field][1], value)) {\r\n                isOk = false;\r\n                document.getElementsByName(\'contactFormField_\' + field)[0].style.border = \"red 1px solid\";\r\n            } else if (value != \"\" && !matchType(fields[field][2], value)) {\r\n                isOk = false;\r\n                document.getElementsByName(\'contactFormField_\' + field)[0].style.border = \"red 1px solid\";\r\n            } else {\r\n                document.getElementsByName(\'contactFormField_\' + field)[0].style.borderColor = \'\';\r\n            }\r\n        } else if (type == \'checkbox\') {\r\n            if (!isRequiredCheckbox(fields[field][1], field)) {\r\n                isOk = false;\r\n            }\r\n        } else if (type == \'checkboxGroup\') {\r\n            if (!isRequiredCheckBoxGroup(fields[field][1], field)) {\r\n                isOk = false;\r\n            }\r\n        } else if (type == \'radio\') {\r\n            if (!isRequiredRadio(fields[field][1], field)) {\r\n                isOk = false;\r\n            }\r\n        }\r\n    }\r\n\r\n    if (!isOk) {\r\n        document.getElementById(\'contactFormError\').style.display = \"block\";\r\n    }\r\n    return isOk;\r\n}\r\nfunction isRequiredNorm(required, value) {\r\n    if (required == 1) {\r\n        if (value == \"\") {\r\n            return true;\r\n        }\r\n    }\r\n    return false;\r\n}\r\nfunction matchType(pattern, value) {\r\n    var reg = new RegExp(pattern);\r\n    if (value.match(reg)) {\r\n        return true;\r\n    }\r\n    return false;\r\n}\r\nfunction isRequiredCheckbox(required, field) {\r\n    if (required == 1) {\r\n        if (!document.getElementsByName(\'contactFormField_\' + field)[0].checked) {\r\n            document.getElementsByName(\'contactFormField_\' + field)[0].style.border = \"red 1px solid\";\r\n            return false;\r\n        }\r\n    }\r\n    document.getElementsByName(\'contactFormField_\' + field)[0].style.borderColor = \'\';\r\n\r\n    return true;\r\n}\r\nfunction isRequiredCheckBoxGroup(required, field) {\r\n    if (required == true) {\r\n        var boxes = document.getElementsByName(\'contactFormField_\' + field + \'[]\');\r\n        var checked = false;\r\n        for (var i = 0; i < boxes.length; i++) {\r\n            if (boxes[i].checked) {\r\n                checked = true;\r\n            }\r\n        }\r\n        if (checked) {\r\n            setListBorder(\'contactFormField_\' + field + \'[]\', false);\r\n            return true;\r\n        } else {\r\n            setListBorder(\'contactFormField_\' + field + \'[]\', \'1px red solid\');\r\n            return false;\r\n        }\r\n    } else {\r\n        return true;\r\n    }\r\n}\r\nfunction isRequiredRadio(required, field) {\r\n    if (required == 1) {\r\n        var buttons = document.getElementsByName(\'contactFormField_\' + field);\r\n        var checked = false;\r\n        for (var i = 0; i < buttons.length; i++) {\r\n            if (buttons[i].checked) {\r\n                checked = true;\r\n            }\r\n        }\r\n        if (checked) {\r\n            setListBorder(\'contactFormField_\' + field, false);\r\n            return true;\r\n        } else {\r\n            setListBorder(\'contactFormField_\' + field, \'1px red solid\');\r\n            return false;\r\n        }\r\n    } else {\r\n        return true;\r\n    }\r\n}\r\nfunction setListBorder(field, borderColor) {\r\n    var boxes = document.getElementsByName(field);\r\n    for (var i = 0; i < boxes.length; i++) {\r\n        if (borderColor) {\r\n            boxes[i].style.border = borderColor;\r\n        } else {\r\n            boxes[i].style.borderColor = \'\';\r\n        }\r\n    }\r\n}\r\n/* ]]> */\r\n</script>','Contact form','Contact form','Contact form','Contact form','index','','',0,'n');
