INSERT INTO `contrexx_module_blog_messages_lang` (`message_id`, `lang_id`, `is_active`, `subject`, `content`, `tags`, `image`) VALUES (3,1,'1','Contrexx - Hersteller bei \"swiss made software\" eingetragen','Internationale Software-Konzerne wie Google, eBay, IBM oder Microsoft haben Schweizerische Qualit&auml;tsmerkmale wie Qualit&auml;t, Zuverl&auml;ssigkeit und Pr&auml;zision in der Software-Entwicklung entdeckt. Mittlerweile habe diese Firmen wichtige Forschungs- und Entwicklungs-Zentren in der Schweiz aufgebaut .<br />\r\n<br />\r\nSeit einigen Monaten hat die Schweiz ein G&uuml;tesiegel und Qualit&auml;tslabel f&uuml;r Softwareprodukte. Der Initiant Luc Haldimann hat dieses G&uuml;tesiegel ins Leben gerufen um die Qualit&auml;t der Schweizer Software auch im Ausland bekannt zu machen.<br />\r\n&quot;swiss made software&quot; ist das neue Label der Schweizer Software-Branche. Die eingetragenen Firmen tragen Sorge zur Qualit&auml;t und entwickeln die entsprechende Software zu mindestens 50% in der Schweiz.','Contrexx swiss made software','');
