INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (881,21,'<div id=\"calendar\">\r\n    <script src=\"lib/datepickercontrol/datepickercontrol.js\" type=\"text/javascript\"></script>\r\n    <script language=\"JavaScript\" type=\"text/javascript\">\r\n    // <![CDATA[\r\n    \r\n    function whole_day()\r\n    {\r\n    	document.getElementById(\'newCalendarNote\').hour.value = \"00\";\r\n    	document.getElementById(\'newCalendarNote\').minutes.value = \"00\";\r\n    	document.getElementById(\'newCalendarNote\').endhour.value = \"23\";\r\n    	document.getElementById(\'newCalendarNote\').endminutes.value = \"59\";\r\n    }\r\n\r\n    DatePickerControl.onSelect = function(inputid){\r\n        var startdate = document.getElementById(\"newCalendarNote\").startdate.value.replace(/-/g,\"\");\r\n        var enddate = document.getElementById(\"newCalendarNote\").enddate.value.replace(/-/g,\"\");\r\n        \r\n        if (startdate > enddate) {\r\n        var date = document.getElementById(\"newCalendarNote\").startdate.value;\r\n        document.getElementById(\"newCalendarNote\").enddate.value = date;\r\n        }\r\n    }\r\n        // ]]>\r\n        </script>\r\n        \r\n    <!-- BEGIN calError -->\r\n    <div class=\"errorbox\" id=\"calError\">{ERROR}</div>\r\n    <!-- END calError -->\r\n    \r\n    <!-- BEGIN calOK -->\r\n    <div class=\"okbox\" id=\"calOK\">{SUCCESS}</div>\r\n    <!-- END calOK -->\r\n    \r\n    <!-- BEGIN addeventForm -->\r\n    <form action=\"index.php?section=calendar{MODULE_INDEX}&amp;cmd=addevent\" method=\"post\"  enctype=\"multipart/form-data\" id=\"newCalendarNote\" name=\"newCalendarNote\" >\r\n        <fieldset>\r\n            <legend>{TXT_CALENDAR_NEW}</legend>\r\n            <p><label>{TXT_CALENDAR_CAT}</label><select name=\"catid\"><option value=\"0\" >- {TXT_CALENDAR_CAT}</option><!-- BEGIN calendar_cat --><option