INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (731,47,'<div id=\"blog\">\r\n	<!-- BEGIN showOkay -->\r\n		<div class=\"message_ok\">{BLOG_DETAILS_COMMENT_OKAY}</div>\r\n	<!-- END showOkay -->\r\n	<!-- BEGIN showError -->\r\n		<div class=\"message_error\">{BLOG_DETAILS_COMMENT_ERROR}</div>\r\n	<!-- END showError -->\r\n	<div class=\"entry\">\r\n		<h2>{BLOG_DETAILS_TITLE}</h2>\r\n		<div class=\"posted\">{BLOG_DETAILS_POSTED_ICON}&nbsp;{BLOG_DETAILS_POSTED}</div>\r\n		<div class=\"content\">\r\n			<div class=\"image\">{BLOG_DETAILS_IMAGE}</div>\r\n			<div class=\"text\">{BLOG_DETAILS_CONTENT}</div>\r\n			<div class=\"networks\">{BLOG_DETAILS_NETWORKS}</div>\r\n		</div>\r\n	</div>\r\n	<!-- BEGIN votingPart -->\r\n		<div class=\"voting\">\r\n			<table cellspacing=\"0\">\r\n				<tr>\r\n					<th colspan=\"2\">{TXT_VOTING}</th>\r\n				</tr>\r\n				<tr class=\"row1\">\r\n					<td width=\"30%\" nowrap=\"nowrap\">{TXT_VOTING_ACTUAL}</td>\r\n					<td width=\"70%\">{BLOG_DETAILS_VOTING_BAR}</td>\r\n				</tr>\r\n				<tr class=\"row2\">\r\n					<td nowrap=\"nowrap\">{TXT_VOTING_AVG}</td>\r\n					<td>{BLOG_DETAILS_VOTING_AVG}</td>\r\n				</tr>\r\n				<tr class=\"row1\">\r\n					<td nowrap=\"nowrap\">{TXT_VOTING_COUNT}</td>\r\n					<td>{BLOG_DETAILS_VOTING_COUNT}</td>\r\n				</tr>\r\n				<tr class=\"row2\">\r\n					<td nowrap=\"nowrap\">{TXT_VOTING_USER}</td>\r\n					<td>{BLOG_DETAILS_VOTING_USER}</td>\r\n				</tr>\r\n			</table>\r\n		</div>\r\n	<!-- END votingPart -->\r\n	<!-- BEGIN commentPart -->\r\n	{BLOG_DETAILS_COMMENTS_JAVASCRIPT}\r\n	<h3>{TXT_COMMENTS}</h3>\r\n	<!-- BEGIN showCommentRows -->\r\n		<div class=\"comments\">\r\n			<div class=\"comment_avatar\">{BLOG_DETAILS_COMMENT_AVATAR}</div>\r\n			<div class=\"comment_title\"><strong>{BLOG_DETAILS_COMMENT_TITLE}</strong></div>\r\n			<div class=\"comment_posted\">{BLOG_DETAILS_COMMENT_POSTED}</div>\r\n		