INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (730,47,'Eine <strong>Tag Cloud</strong> ist eine Methode zur Informationsvisualisierung, bei der eine Liste aus Schlagworten zweidimensional alphabetisch sortiert angezeigt wird, wobei einzelne unterschiedlich gewichtete Worte gr&ouml;sser oder auf andere Weise hervorgehoben dargestellt werden. Wortwolken werden zunehmend beim gemeinschaftlichen Indexieren und in Blogs eingesetzt. [<a target=\"_blank\" href=\"http://de.wikipedia.org/wiki/Tag_cloud\">Quelle</a>]\r\n<br /><br /><fieldset><legend>Beliebteste Tags</legend>{BLOG_TAG_HITLIST}</fieldset>\r\n<fieldset><legend>Tag Cloud</legend>{BLOG_TAG_CLOUD}</fieldset>','Tag Cloud','cloud','y',728,'on','system',1,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (260,0,'<div id=\"blog\">\r\n	<div class=\"text\">Das Contrexx Blog-Modul erzeugt, falls in den Moduleinstellungen aktiviert, automatisch RSS-Feeds von Ihren Meldungen und den dazu verfassten Kommentaren. Dabei wird pro aktivierte Systemsprache im Ordner <strong>feed/</strong> eine eigene XML-Datei nach folgendem Schema erstellt:</div>\r\n	<div class=\"code\">\r\n<pre>blog_messages_XX.xml\r\nblog_comments_XX.xml\r\nblog_category_ID_XX.xml</pre>\r\n	</div>\r\n	<div class=\"text\">Dabei steht der Platzhalter XX f&uuml;r die Kurzform der jeweiligen Sprache und ID f&uuml;r die eindeutige Bezeichnungs-ID der jeweiligen Kategorie. F&uuml;r die Sprache Deutsch (Kurzform: de) lauten der komplette Link beispielsweise wie folgt:</div>\r\n	<div class=\"code\">\r\n<pre>http://www.Ihre-Website.ch/feed/blog_messages_de.xml\r\nhttp://www.Ihre-Website.ch/feed/blog_comments_de.xml</pre>\r\n	</div>\r\n	<div class=\"text\">F&uuml;r die Kategorie 1 (ID = 1) in der Sprache deutsch w&uuml;rde der Pfad demnach wie folgt lauten:</div>\r\n	<div class=\"code\">\r\n<pre>http://www.Ihre-Website.ch/feed/blog_category_1_de.xml</pre>\r\n	</div>\r\n	<div class=\"text\">Eine komplette &Uuml;bersicht aller Kurzformen finden Sie in Ihrem Administrationsbereich unter <strong>Administration &gt; Spracheinstellungen</strong>.</div>\r\n</div>','RSS-Feed','','y',257,'on','system',3,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (728,47,'<div id=\"blog\">\r\n	<div class=\"categories\">\r\n		<h2>Themen Kategorien</h2>\r\n		{BLOG_CATEGORIES_LIST}\r\n	</div>\r\n	<!-- BEGIN showBlogEntries -->\r\n		<div class=\"entries\">\r\n			<h2><a title=\"{BLOG_ENTRIES_TITLE}\" href=\"index.php?section=blog&amp;cmd=details&amp;id={BLOG_ENTRIES_ID}\">{BLOG_ENTRIES_TITLE}</a></h2>\r\n			<div class=\"posted\">\r\n				{BLOG_ENTRIES_POSTED_ICON}&nbsp;{BLOG_ENTRIES_POSTED}\r\n				<!-- BEGIN showCommentPart -->\r\n					&nbsp;&nbsp;|&nbsp;&nbsp;<a title=\"{TXT_BLOG_COMMENTS}\" href=\"index.php?section=blog&amp;cmd=details&amp;id={BLOG_ENTRIES_ID}#comments\">{BLOG_ENTRIES_COMMENTS}</a>\r\n				<!-- END showCommentPart -->\r\n                                <br />{BLOG_ENTRIES_TAGS_ICON}&nbsp;{BLOG_ENTRIES_TAGS}\r\n			</div>\r\n			<div class=\"content\">\r\n				<div class=\"image\">{BLOG_ENTRIES_IMAGE}</div>\r\n				<div class=\"text\">{BLOG_ENTRIES_INTRODUCTION}</div>\r\n				<div class=\"link\"><a href=\"index.php?section=blog&amp;cmd=details&amp;id={BLOG_ENTRIES_ID}\">Weiter...</a></div>\r\n			</div>\r\n			<div class=\"statistics\">\r\n				<strong>{TXT_BLOG_CATEGORIES}:</strong>&nbsp;{BLOG_ENTRIES_CATEGORIES}<br />\r\n				<!-- BEGIN showVotingPart -->\r\n					<strong>{TXT_BLOG_VOTING}:</strong>&nbsp; <a title=\"{TXT_BLOG_VOTING_DO}\" href=\"index.php?section=blog&amp;cmd=details&amp;id={BLOG_ENTRIES_ID}#voting\">{BLOG_ENTRIES_VOTING_STARS}</a>&nbsp;(<a title=\"{TXT_BLOG_VOTING_DO}\" href=\"index.php?section=blog&amp;cmd=details&amp;id={BLOG_ENTRIES_ID}#voting\">{BLOG_ENTRIES_VOTING}</a>) \r\n				<!-- END showVotingPart -->\r\n			</div>\r\n		</div>\r\n	<!-- END showBlogEntries -->\r\n	{BLOG_ENTRIES_PAGING}\r\n</div>','Blog','','y',0,'on','system',30,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (729,47,'<div id=\"blog\">\r\n	{BLOG_SEARCH_JAVASCRIPT}\r\n	<div class=\"searchform\">\r\n		<form action=\"index.php?section=blog&amp;cmd=search\" name=\"frmDoSearch\" method=\"post\">\r\n            <fieldset>\r\n                <legend>Kategorien durchsuchen</legend>\r\n                <p><label>{TXT_SEARCH_MODUS_KEYWORD}</label><input type=\"text\" value=\"{BLOG_SEARCH_KEYWORD_STRING}\" name=\"frmDoSearch_Keyword_String\" /></p>\r\n                <p><label>Kategorie</label>{BLOG_SEARCH_KEYWORD_CATEGORIES}</p>\r\n                <p><input type=\"submit\" value=\"{TXT_SEARCH_SUBMIT}\" name=\"frmDoSearch_Submit\" /></p>\r\n            </fieldset>\r\n            <fieldset>\r\n                <legend>Suche nach Datum</legend>\r\n                <p><div class=\"search_date\">{BLOG_SEARCH_DATE_CALENDAR}</div></p>\r\n            </fieldset>\r\n		</form>\r\n	</div>\r\n	<!-- BEGIN ResultPart -->\r\n		<h2>{TXT_SEARCH_RESULTS}</h2>\r\n		<!-- BEGIN showResults -->\r\n			<div class=\"entries\">\r\n				<h2><a title=\"{BLOG_SEARCH_RESULTS_SUBJECT}\" href=\"index.php?section=blog&amp;cmd=details&amp;id={BLOG_SEARCH_RESULTS_MID}\">{BLOG_SEARCH_RESULTS_SUBJECT}</a></h2>\r\n				<div class=\"content\">\r\n					<div class=\"text\">{BLOG_SEARCH_RESULTS_INTRODUCTION}</div>\r\n					<div class=\"link\"><a href=\"index.php?section=blog&amp;cmd=details&amp;id={BLOG_SEARCH_RESULTS_MID}\">Weiter...</a></div>\r\n				</div>\r\n				<div class=\"statistics\">\r\n					<strong>{TXT_SEARCH_RESULTS_DATE}:</strong>&nbsp;{BLOG_SEARCH_RESULTS_POSTED}<br />\r\n					<strong>{TXT_SEARCH_RESULTS_CATEGORY}:</strong>&nbsp;{BLOG_SEARCH_RESULTS_CATEGORIES}<br />\r\n					<strong>{TXT_SEARCH_RESULTS_KEYWORDS}:</strong>&nbsp; {BLOG_SEARCH_RESULTS_TAGS} \r\n				</div>\r\n			</div>\r\n		<!-- END showResults -->\r\n		<!-- BEGIN showNoResults -->\r\n