INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (765,33,'<script type=\"text/javascript\">\r\n	<!--\r\n	function toggle(target){\r\n		obj = document.getElementById(target);\r\n		obj.style.display = (obj.style.display==\'none\') ? \'inline\' : \'none\';  \r\n		if (obj.style.display==\'none\' && target == \'hiddenSearch\'){\r\n			 document.getElementById(\'searchCheck\').value = \'norm\';\r\n		}else if(obj.style.display==\'inline\' && target == \'hiddenSearch\'){\r\n			 document.getElementById(\'searchCheck\').value = \'exp\';\r\n		}\r\n	}\r\n	\r\n	function CheckForm() {\r\n	  var errorMsg = \"\";\r\n	  with( document.formEntry ) {\r\n		if (type.value == \"\") {\r\n			errorMsg = errorMsg + \"- {TXT_MARKET_TYPE}\\n\";\r\n		}\r\n		if (title.value == \"\") {\r\n			errorMsg = errorMsg + \"- {TXT_MARKET_TITLE_ENTRY}\\n\";\r\n		} \r\n		if (description.value == \"\") {\r\n			errorMsg = errorMsg + \"- {TXT_MARKET_DESCRIPTION}\\n\";\r\n		}   \r\n		if (cat.value == \"\") {\r\n			errorMsg = errorMsg + \"- {TXT_MARKET_CATEGORIE}\\n\";\r\n		} \r\n		\r\n		{END_DATE_JS}\r\n	\r\n		if (userdetails.value == \"\") {\r\n			errorMsg = errorMsg + \"- {TXT_MARKET_USER_DETAIL}\\n\";\r\n		}\r\n		if (name.value == \"\") {\r\n			errorMsg = errorMsg + \"- {TXT_MARKET_NAME}\\n\";\r\n		}\r\n		if (email.value == \"\") {\r\n			errorMsg = errorMsg + \"- {TXT_MARKET_EMAIL}\\n\";\r\n		}else{\r\n			if (!matchType(\'^[_a-zA-Z0-9-]+(.[_a-zA-Z0-9-]+)*@[a-zA-Z0-9-]+(.[a-zA-Z0-9-]+)*.(([0-9]{1,3})|([a-zA-Z]{2,3})|(aero|coop|info|museum|name))$\', email.value)){\r\n				errorMsg = errorMsg + \"- {TXT_MARKET_EMAIL}\\n\";\r\n			}\r\n		}\r\n		\r\n		if (errorMsg != \"\") \r\n		{\r\n			alert (\"{TXT_MARKET_FIELDS_REQUIRED}n{TXT_MARKET_THOSE_FIELDS_ARE_EMPTY}\\n\" + errorMsg);       \r\n			return false;\r\n		}else{\r\n			return true;\r\n		}\r\n	  }\r\n	}\r\n	\r\n	function matchType(pattern, value) {\r\n		var reg = new RegExp(pattern);\r\n		if (value.match(reg)) {\r\n			return true;\r\n		}\r\n		return false;\r\n	} \r\n	\r\n	-->\r\n	</script>\r\n<div id=\"market\">\r\n	<form name=\"formEntry\" enctype=\"multipart/form-data\" method=\"post\" action=\"index.php?section=market&cmd=confirm\" onsubmit=\"return CheckForm()\">\r\n	<input type=\"hidden\" name=\"id\" value=\"{MARKET_ENTRY_ID}\" >\r\n        <p><label>{TXT_MARKET_TYPE}<font color=\"red\"> *</font></label><input type=\"radio\" name=\"type\" value=\"offer\" {MARKET_ENTRY_TYPE_OFFER} /><label class=\"description\" for=\"offer\">{TXT_MARKET_OFFER}</label></p>\r\n        <p><input type=\"radio\" name=\"type\" value=\"search\" {MARKET_ENTRY_TYPE_SEARCH} /><label class=\"description\" for=\"search\">{TXT_MARKET_SEARCH}</label></p>\r\n        <p><label for=\"title\">{TXT_MARKET_TITLE_ENTRY}<font color=\"red\"> *</font></label><input id=\"title\" name=\"title\" value=\"\" type=\"text\"></p>\r\n        <p><label for=\"description\">{TXT_MARKET_DESCRIPTION}<font color=\"red\"> *</font></label><textarea id=\"description\" name=\"description\"></textarea></p>\r\n        <p><label for=\"premium\">{TXT_MARKET_PREMIUM}</label><input type=\"checkbox\" id=\"premium\" name=\"premium\" value=\"1\" /></p>\r\n        <p><label for=\"pic\">{TXT_MARKET_PICTURE}</label>{ENTRY_PICTURE}<input type=\"file\" id=\"pic\" name=\"pic\"></p>\r\n        <p><label for=\"cat\">{TXT_MARKET_CATEGORIE}<font color=\"red\"> *</font></label><select id=\"cat\" name=\"cat\">{MARKET_CATEGORIES}</select></p>\r\n        <p><label for=\"price\">{TXT_MARKET_PRICE}<font color=\"red\"> *</font></label><input type=\"text\" id=\"price\" name=\"price\" maxlength=\"100\" value=\"\"></p>\r\n        <p><input type=\"checkbox\" name=\"forfree\" value=\"1\" /><label class=\"description\" for=\"forfree\">{TXT_MARKET_FOR_FREE}</label></p>\r\n        <p><input type=\"checkbox\" name=\"agreement\" value=\"1\" /><label class=\"description\" for=\"agreement\">{TXT_MARKET_AGREEMENT}</label></p>\r\n        <!-- BEGIN end_date_dropdown -->\r\n            <p><label for=\"days\">{TXT_MARKET_END_DATE}<font color=\"red\"> *</font></label><select id=\"days\" name=\"days\" style=\"width: 100px;\">{MARKET_DAYS_ONLINE}</select></p>\r\n        <!-- END end_date_dropdown -->\r\n        <p><label for=\"name\">{TXT_MARKET_NAME}<font color=\"red\"> *</font></label><input id=\"name\" name=\"name\" value=\"\" type=\"text\"></p>\r\n        <p><label for=\"email\">{TXT_MARKET_EMAIL}<font color=\"red\"> *</font></label><input id=\"email\" name=\"email\" value=\"\" type=\"text\"></p>\r\n        <p><label>{TXT_MARKET_USER_DETAIL}<font color=\"red\"> *</font></label><input type=\"radio\" id=\"userdetails_on\" name=\"userdetails\" value=\"1\" {MARKET_ENTRY_USERDETAILS_ON} /><label class=\"description\" for=\"userdetails_on\">{TXT_MARKET_DETAIL_SHOW}</label></p>\r\n        <p><input type=\"radio\" name=\"userdetails\" id=\"userdetails_off\" value=\"0\" {MARKET_ENTRY_USERDETAILS_OFF} /><label class=\"description\" for=\"userdetails_off\">{TXT_MARKET_DETAIL_HIDE}</label></p>\r\n        <!-- BEGIN spez_fields -->\r\n            <p><label>{TXT_MARKET_SPEZ_FIELD_NAME}</label>{MARKET_SPEZ_FIELD_INPUT}</p>\r\n        <!-- END spez_fields -->\r\n        <p><input type=\"submit\" name=\"submitEntry\" value=\"{TXT_MARKET_SAVE}\" /></p>\r\n	</form>\r\n</div>','Inserat erstellen','add','y',764,'on','system',1,'1');
