INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (809,58,'<script type=\"text/javascript\">\r\n<!--\r\nfunction toggle(target){\r\n    obj = document.getElementById(target);\r\n    obj.style.display = (obj.style.display==\'none\') ? \'inline\' : \'none\';  \r\n    if (obj.style.display==\'none\' && target == \'hiddenSearch\'){\r\n         document.getElementById(\'searchCheck\').value = \'norm\';\r\n    }else if(obj.style.display==\'inline\' && target == \'hiddenSearch\'){\r\n         document.getElementById(\'searchCheck\').value = \'exp\';\r\n    }\r\n}\r\n\r\nfunction CheckForm() {\r\n  var errorMsg = \"\";\r\n  with( document.sendMessage) {\r\n    if (title.value == \"\") {\r\n    	errorMsg = errorMsg + \"- {TXT_AUCTION_TITLE} <n\";\r\n    }\r\n    if (message.value == \"\") {\r\n        errorMsg = errorMsg + \"- {TXT_AUCTION_MSG}\\n\";\r\n    } \r\n    if (email.value == \"\") {\r\n        errorMsg = errorMsg + \"- {TXT_AUCTION_EMAIL}\\n\";\r\n    }else{\r\n    	if (!matchType(\'^[_a-zA-Z0-9-]+(.[_a-zA-Z0-9-]+)*@[a-zA-Z0-9-]+(.[a-zA-Z0-9-]+)*.(([0-9]{1,3})|([a-zA-Z]{2,3})|(aero|coop|info|museum|name))$\', email.value)){\r\n    		errorMsg = errorMsg + \"- n\";\r\n    	}\r\n    }\r\n\r\n    if (errorMsg != \"\") \r\n    {\r\n        alert (\"{TXT_FIELDS_REQUIRED}n{TXT_THOSE_FIELDS_ARE_EMPTY}\\n\" + errorMsg);       \r\n        return false;\r\n    }else{\r\n    	return true;\r\n    }\r\n  }\r\n}\r\n\r\n\r\nfunction matchType(pattern, value) {\r\n	var reg = new RegExp(pattern);\r\n	if (value.match(reg)) {\r\n		return true;\r\n	}\r\n	return false;\r\n} \r\n\r\nfunction update_countdown() {\r\n	new Ajax.PeriodicalUpdater(\'countdown\', \'modules/auction/lib/counter.class.php?mode=countdown&id={AUCTION_ID}\', {method: \'get\', frequency: 1, showProgress: 0});\r\n}\r\n\r\nfunction QuestionDiv(loged){\r\n	if(loged==1){\r\n		if(document.getElementById(\"question_div\") && document.getElementById(\"question_div\").style.display != \'block\'){\r\n			document.getElementById(\"question_div\").style.display = \'block\';\r\n		}else{\r\n			document.getElementById(\"question_div\").style.display = \'none\';\r\n		}\r\n	}else{\r\n		alert(\"{TXT_AUCTION_NOT_LOGED_ERROR}\");\r\n	}\r\n}\r\n\r\nfunction SendQuestion(){\r\n	objFrm = document.frmQuestion;\r\n	if(objFrm.Name.value==\'\' || objFrm.Email.value==\'\' || objFrm.Frage.value==\'\'){\r\n		alert(\"{TXT_AUCTION_ADD_ALL_FIELDS}\");\r\n	}else{\r\n		objFrm.submit();\r\n	}\r\n}\r\n\r\nfunction MakeOffer(){\r\n	objFrm = document.frmMakeOffer;\r\n	if(objFrm.loged.value!=1){\r\n		alert(\"{TXT_AUCTION_NOT_LOGED_ERROR}\");\r\n	}else{\r\n		objFrm.submit();\r\n	}\r\n}\r\n-->\r\n</script>\r\n<div id=\"auction\">\r\n	<div class=\"search\">\r\n		<h1>&nbsp;{TXT_AUCTION_SEARCH_TITLE}</h1>\r\n		<form action=\"index.php?\" method=\"get\" name=\"auctionSearch\" id=\"auctionSearch\">\r\n			<input name=\"term\" value=\"{TXT_AUCTION_SEARCHTERM}\" size=\"25\" maxlength=\"100\" /><input type=\"hidden\" id=\"searchCheck\" name=\"check\" value=\"norm\" size=\"10\" /><input type=\"hidden\" name=\"section\" value=\"auction\" size=\"10\" /><input type=\"hidden\" name=\"cmd\" value=\"search\" size=\"10\" />&nbsp;<input type=\"submit\" value=\"{TXT_AUCTION_SEARCH}\" name=\"search\" /> \r\n			\r\n		</form>\r\n	</div>\r\n	<br />\r\n	<div class=\"navtree\"><a href=\"?section=auction\">{TXT_AUCTION}</a>{AUCTION_CATEGORY_NAVI}</div>\r\n	<br />\r\n	<div class=\"detail\">\r\n	\r\n		<h1>&nbsp;{AUCTION_TITLE}</h1>\r\n		\r\n		<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\">\r\n			<tr>\r\n				<td valign=\"top\" width=\"200\">{AUCTION_TMBS}</td>\r\n				<td valign=\"top\">\r\n					<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\">\r\n						<tr>\r\n							<td width=\"150\"><b>{TXT_AUCTION_ID}</b></td>\r\n							<td>{AUCTION_ID}</td>\r\n						</tr>\r\n						<tr>\r\n							<td><b>{TXT_AUCTION_ENDTIME}</b></td>\r\n							<td>{AUCTION_ENDTIME}</td>\r\n						</tr>\r\n						<tr>\r\n							<td><b>{TXT_AUCTION_NR_BIDS}</b></td>\r\n							<td>{AUCTION_NR_BIDS}</td>\r\n						</tr>\r\n					</table>\r\n					<br />\r\n					<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\">\r\n						<tr>\r\n							<td width=\"150\"><b>{TXT_AUCTION_STARTPRICE}</b></td>\r\n							<td>{AUCTION_STARTPRICE}</td>\r\n						</tr>\r\n						<tr>\r\n							<td><b>{TXT_AUCTION_INCREASE_STEP}</b></td>\r\n							<td>{AUCTION_INCREASE_STEP}</td>\r\n						</tr>\r\n						<tr>\r\n							<td><b>{TXT_AUCTION_NEXT_BID}</b></td>\r\n							<td>{AUCTION_NEXT_BID}</td>\r\n						</tr>\r\n						<tr>\r\n							<td><b>{TXT_AUCTION_DURATION}</b></td>\r\n							<td><div id=\"countdown\">Countdown</div></td>\r\n						</tr>\r\n					</table>\r\n					<br />\r\n					<!-- Make offer begin-->\r\n					<div id=\"login_offer\" style=\"display: {DISPLAY_LOGIN};\">\r\n						<form action=\"index.php?section=login\" method=\"post\">\r\n						<input name=\"redirect\" value=\"{REDIRECT_URL}\" type=\"hidden\" />\r\n						<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" style=\"background-color: #E4E4E4;\">\r\n							<tr>\r\n								<td width=\"150\"><b>{TXT_AUCTION_USENAME}</b></td>\r\n								<td><input type=\"text\" value=\"\" name=\"USERNAME\" style=\"width: 100px;\" /></td>\r\n							</tr>\r\n							<tr>\r\n								<td width=\"150\"><b>{TXT_AUCTION_PASSWORD}</b></td>\r\n								<td><input type=\"password\" value=\"\" name=\"PASSWORD\" style=\"width: 100px;\" /></td>\r\n							</tr>\r\n							<tr>\r\n								<td width=\"150\">&nbsp;</td>\r\n								<td><input type=\"submit\" value=\"{TXT_LOGIN}\" name=\"login\" /></td>\r\n							</tr>\r\n						</table>\r\n						</form>\r\n					</div>\r\n					<form name=\"frmMakeOffer\" method=\"post\" action=\"{TXT_AUCTION_URL}\">\r\n					<input type=\"hidden\" name=\"loged\" value=\"{LOGED}\" />\r\n					<input type=\"hidden\" name=\"exe\" value=\"makeOffer\" />\r\n					<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" style=\"background-color: #E4E4E4;\">\r\n						<tr>\r\n							<td width=\"150\"><b>{TXT_AUCTION_YOUR_OFFER}</b></td>\r\n							<td><input type=\"text\" value=\"{YOUR_OFFER_1}\" name=\"offer_1\" style=\"width: 75px; text-align: right;\" />.<input type=\"text\" value=\"{YOUR_OFFER_2}\" maxlength=\"2\" name=\"offer_2\" style=\"width: 15px;\" /> {CURRENCY}</td>\r\n						</tr>\r\n						<tr>\r\n							<td>&nbsp;</td>\r\n							<td><input type=\"button\" value=\"{TXT_AUCTION_ADD_OFFER}\" onclick=\"MakeOffer();\" /></td>\r\n						</tr>\r\n					</table>\r\n					</form>\r\n					<!-- Make offer end-->\r\n				</td>\r\n			</tr>\r\n		</table>\r\n		\r\n		<br />\r\n		<h2>&nbsp;{TXT_AUCTION_DESCRIPTION}:</h2>\r\n		{AUCTION_DESCRIPTION}\r\n		\r\n		<br /><br />\r\n		\r\n		<h2>&nbsp;{TXT_AUCTION_INFORMATIONEN}:</h2>\r\n		<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\">\r\n			<tr>\r\n				<td width=\"150\"><b>{TXT_AUCTION_SHIPPING}</b></td>\r\n				<td>{AUCTION_SHIPPING}</td>\r\n			</tr>\r\n			<tr>\r\n				<td><b>{TXT_AUCTION_PAYMENT}</b></td>\r\n				<td>{AUCTION_PAYMENT}</td>\r\n			</tr>\r\n		</table>\r\n		\r\n		<br />\r\n		\r\n		<h2>&nbsp;{TXT_AUCTION_OFFERS}:</h2>\r\n		\r\n		<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\">\r\n			<tr>\r\n				<th width=\"20\">&nbsp;</th>\r\n				<th><b>{TXT_AUCTION_USENAME}</b></th>\r\n				<th align=\"center\"><b>{TXT_AUCTION_BID}</b></th>\r\n				<th align=\"right\"><b>{TXT_AUCTION_BID_MAKED}</b></th>\r\n			</tr>\r\n			<!-- BEGIN AllOffers -->\r\n			<tr>\r\n				<td><img src=\"{AUCTION_OFFERS_ICON}\" border=\"0\" alt=\"\" /></td>\r\n				<td>{AUCTION_USENAME}</td>\r\n				<td align=\"center\">{AUCTION_BID}</td>\r\n				<td align=\"right\">{AUCTION_BID_MAKED}</td>\r\n			</tr>	\r\n			<!-- END AllOffers -->\r\n		</table>\r\n		\r\n		\r\n		\r\n		<br /><br />\r\n		\r\n		<h2>&nbsp;{TXT_AUCTION_QUESTIONS}:</h2>\r\n		\r\n		<a href=\"javascript:QuestionDiv({LOGED});\">> {TXT_AUCTION_QUESTIONS}</a>\r\n		\r\n		<div style=\"display: none;\" id=\"question_div\">\r\n			<form name=\"frmQuestion\" method=\"post\" action=\"{TXT_AUCTION_URL}\">\r\n				<input type=\"hidden\" name=\"exe\" value=\"sendQuestion\" />\r\n				<table cellspacing=\"0\" cellpadding=\"2\" border=\"0\">\r\n					<tr>\r\n						<td width=\"150\"><b>{TXT_AUCTION_NAME}</b></td>\r\n						<td><input type=\"text\" name=\"Name\" value=\"\" style=\"width: 300px;\" /></td>\r\n					</tr>\r\n					<tr>\r\n						<td width=\"150\"><b>{TXT_AUCTION_EMAIL}</b></td>\r\n						<td><input type=\"text\" name=\"Email\" value=\"\" style=\"width: 300px;\" /></td>\r\n					</tr>\r\n					<tr>\r\n						<td width=\"150\"><b>{TXT_AUCTION_QUESTION}</b></td>\r\n						<td><textarea name=\"Frage\" style=\"width: 300px;\" rows=\"5\"></textarea></td>\r\n					</tr>\r\n					<tr>\r\n						<td width=\"150\">&nbsp;</td>\r\n						<td><input type=\"button\" value=\"{TXT_AUCTION_SEND_QUESTION}\" onclick=\"SendQuestion();\" /></td>\r\n					</tr>\r\n				</table>\r\n			</form>\r\n		</div>\r\n		\r\n		<!-- Backup -->\r\n		<div style=\"display: none;\">\r\n		\r\n		<div class=\"data\">\r\n			<div class=\"image\">{AUCTION_PICTURE}</div>\r\n			<div class=\"description\"><b>{AUCTION_TYPE}</b>&nbsp;<i>{AUCTION_ENDDATE}&nbsp;</i><br /><br />{AUCTION_DESCRIPTION}</div>\r\n		</div> \r\n		\r\n		<div class=\"data\">\r\n			<table cellspacing=\"0\">\r\n				<tr>\r\n					<th colspan=\"2\">zus&auml;tzliche Informationen</th>\r\n				</tr>\r\n				<tr class=\"auctionRow2\">\r\n					<td width=\"120\" nowrap=\"nowrap\">{TXT_AUCTION_PRICE}</td>\r\n					<td>{AUCTION_PRICE}</td>\r\n				</tr>\r\n				<tr class=\"auctionRow3\">\r\n					<td nowrap=\"nowrap\">{TXT_AUCTION_USER_DETAILS}</td>\r\n					<td>{AUCTION_USER_DETAILS}</td>\r\n				</tr>\r\n			</table>\r\n			\r\n		</div>\r\n		\r\n		</div>\r\n	\r\n	</div>\r\n	\r\n	<div style=\"clear: both;\"><br /></div>\r\n	\r\n	<div style=\"display: none;\">\r\n	\r\n	<div class=\"form\">\r\n		<fieldset> \r\n			<legend>{TXT_AUCTION_MESSAGE}</legend>\r\n			<form name=\"sendMessage\" action=\"?section=auction&amp;cmd=send&amp;id={AUCTION_ID}\" method=\"post\" onsubmit=\"return CheckForm()\">\r\n				<p><label for=\"title\">{TXT_AUCTION_TITLE}</label><input name=\"title\" style=\"width: 300px;\" value=\"{TXT_AUCTION_MSG_TITLE}{AUCTION_TITLE}\" /></p>\r\n				<p><label for=\"name\">{TXT_AUCTION_NAME}</label><input name=\"name\" style=\"width: 300px;\" value=\"\" /></p>\r\n				<p><label for=\"email\">{TXT_AUCTION_EMAIL}</label><input name=\"email\" style=\"width: 300px;\" value=\"\" /></p>\r\n				<p><label for=\"price\">{TXT_AUCTION_PRICE_MSG}</label><input name=\"price\" style=\"width: 300px;\" value=\"\" /></p>\r\n				<p><label for=\"newprice\">{TXT_AUCTION_NEW_PRICE}</label><input name=\"newprice\" style=\"width: 300px;\" value=\"\" /></p>\r\n				<p><label for=\"message\">{TXT_AUCTION_MSG}</label><textarea name=\"message\" style=\"width:300px; overflow: auto;\" rows=\'7\'></textarea></p>\r\n				<p><input type=\"submit\" name=\"sendMSG\" value=\"{TXT_AUCTION_SEND}\" /></p>\r\n			</form>\r\n		</fieldset>\r\n	</div>\r\n	<div class=\"functions\">{AUCTION_EDIT}&nbsp;|&nbsp;{AUCTION_DEL}</div>\r\n	\r\n	</div>\r\n</div>\r\n\r\n<script type=\"text/javascript\">\r\n<!--\r\nupdate_countdown();\r\n{ADD_JS}\r\n-->\r\n</script>\r\n\r\n','Auktion Details','detail','y',803,'off','grimm',99,'1');
