INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (867,8,'<h3>Online Integration using the RSS-Newsfeed</h3>\r\n<p><a class=\"rssfeed\" title=\"RSS Newsfeed of {NEWS_HOSTNAME}\" href=\"{NEWS_RSS_FEED_URL}\" style=\"\"><img width=\"48\" height=\"74\" border=\"0\" alt=\"The feed of the website can be requested using this link\" src=\"images/content/icons/rss_bg.gif\" style=\"margin: 5px 20px 20px; float: right;\" /></a>RSS is an electronic news format which allows users to receive updates from a website without the need of visiting the page at all.</p>\r\n<h3>Online Integration using Javascript</h3>\r\nThe feed of this website can also be displayed in your website in a very simple way. Simple put the following code into your website:<br />\r\n<br />\r\n<form action=\"\">\r\n    <textarea name=\"code\" cols=\"30\" rows=\"18\" style=\"width: 98%; font-size: 95%;\">{NEWS_RSS2JS_CODE}</textarea>     <br />\r\n    <br />\r\n    <input type=\"button\" name=\"button\" onclick=\"javascript:this.form.code.focus();this.form.code.select();\" value=\"Select all\" />\r\n</form>','News Feed','feed','n',865,'on','system',20,'2');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (868,8,'<font color=\"#ff0000\">{NEWS_STATUS_MESSAGE}&nbsp;</font> \r\n<!-- BEGIN news_submit_form -->\r\n<form method=\"post\" action=\"index.php?section=news&amp;cmd=submit\">\r\n    <fieldset> \r\n    <legend>{TXT_NEWS_MESSAGE}</legend>\r\n    <p><label for=\"newsTitle\">{TXT_TITLE}</label><input id=\"newsTitle\" name=\"newsTitle\" value=\"{NEWS_TITLE}\" type=\"text\"/></p>\r\n    <p><label for=\"newsCat\">{TXT_CATEGORY}</label><select id=\"newsCat\" name=\"newsCat\"><!-- BEGIN news_category_menu --><option value=\"{NEWS_CATEGORY_ID}\">{NEWS_CATEGORY_TITLE}</option><!-- END news_category_menu --></select></p>\r\n    <p><label for=\"newsSource\">{TXT_EXTERNAL_SOURCE}</label><input id=\"newsSource\" name=\"newsSource\" value=\"{NEWS_SOURCE}\" type=\"text\"/></p>\r\n    <p><label for=\"newsUrl1\">{TXT_LINK} #1</label><input id=\"newsUrl1\" name=\"newsUrl1\" value=\"{NEWS_URL1}\" type=\"text\"/></p>\r\n    <p><label for=\"newsUrl2\">{TXT_LINK} #2</label><input id=\"newsUrl2\" name=\"newsUrl2\" value=\"{NEWS_URL2}\" type=\"text\"/></p>\r\n    <p><label for=\"newsTeaserText\">{TXT_NEWS_TEASER_TEXT}</label><textarea id=\"newsTeaserText\" name=\"newsTeaserText\">{NEWS_TEASER_TEXT}</textarea></p>\r\n    </fieldset> \r\n    <fieldset> \r\n    <legend>{TXT_NEWS_NEWS_CONTENT}</legend>\r\n    {TXT_NEWS_REDIRECT}<br /><br />\r\n    <p><label for=\"newsRedirect\">{TXT_NEWS_NEWS_URL}</label><input id=\"newsRedirect\" name=\"newsRedirect\" value=\"{NEWS_REDIRECT}\" type=\"text\" /></p>\r\n    {NEWS_TEXT}\r\n    <p><label>Captcha</label><img style=\"padding-top: 5px; padding-bottom: 5px;\" src=\"{IMAGE_URL}\" alt=\"{IMAGE_ALT}\" title=\"{IMAGE_ALT}\" /><br />{TXT_CAPTCHA}</p>\r\n    <p><input type=\"text\" name=\"captcha\" id=\"captcha\"/><input type=\"hidden\" name=\"offset\" value=\"{CAPTCHA_OFFSET}\" /></p>\r\n    <p><input type=\"reset\" onclick=\"FCKeditorAPI.GetInstance(\'newsText\').SetHTML(\'\')\" /> <input type=\"submit\" name=\"submitNews\" value=\"Submit\" /></p>\r\n    </fieldset> \r\n</form>\r\n<!-- END news_submit_form --> \r\n<!-- BEGIN news_submitted --> \r\n<a title=\"submit an other message\" href=\"?section=news&amp;cmd=submit\">Click here to submit an other message.</a> \r\n<!-- END news_submitted -->','Submit message','submit','y',865,'on','system',30,'2');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (682,3,'<script language=\"javascript\" type=\"text/javascript\">\r\nShadowbox.loadSkin(\'classic\',\'lib/javascript/shadowbox/src/skin/\');\r\nShadowbox.loadLanguage(\'en\', \'lib/javascript/shadowbox/src/lang\');\r\nShadowbox.loadPlayer([\'flv\', \'html\', \'iframe\', \'img\', \'qt\', \'swf\', \'wmp\'], \'lib/javascript/shadowbox/src/player\');\r\n</script>\r\n\r\n<script language=\"javascript\" type=\"text/javascript\">\r\nwindow.onload = function(){\r\n    Shadowbox.init();\r\n};\r\n</script>\r\n\r\n<div id=\"gallery\">\r\n    <!-- BEGIN galleryCategories -->\r\n    <div class=\"category_tree\">You are here: {GALLERY_CATEGORY_TREE}</div>\r\n    \r\n    <!-- BEGIN galleryCategoryList -->\r\n    <div class=\"category row_{GALLERY_STYLE}\">\r\n        <div class=\"category_image\">{GALLERY_CATEGORY_IMAGE}</div>\r\n        <div class=\"category_informations\">\r\n            <h2>{GALLERY_CATEGORY_NAME}</h2>\r\n            {GALLERY_CATEGORY_DESCRIPTION}<br />\r\n            <!-- <br />\r\n            {GALLERY_CATEGORY_INFO} -->\r\n        </div>\r\n    </div>\r\n    <!-- END galleryCategoryList --> 	\r\n    \r\n    <!-- BEGIN galleryImageBlock -->  	\r\n    {GALLERY_JAVASCRIPT}\r\n    <div class=\"category_comment\">{GALLERY_CATEGORY_COMMENT}</div>\r\n    <!-- BEGIN galleryShowImages -->\r\n    <div class=\"category_images\">\r\n        <div class=\"image\">{GALLERY_IMAGE1}<br />{GALLERY_IMAGE_LINK1}</div>\r\n        <div class=\"image\">{GALLERY_IMAGE2}<br />{GALLERY_IMAGE_LINK2}</div>\r\n        <div class=\"image\">{GALLERY_IMAGE3}<br />{GALLERY_IMAGE_LINK3}</div>\r\n    </div>\r\n    <!-- END galleryShowImages -->\r\n\r\n    <div class=\"category_paging\">{GALLERY_PAGING}</div>\r\n    <!-- END galleryImageBlock --> \r\n     	\r\n    <!-- END galleryCategories -->   	\r\n    \r\n    <!-- BEGIN galleryImage -->\r\n    <div class=\"category_tree\">You are here: {GALLERY_CATEGORY_TREE}</div>\r\n    <div class=\"show_image\" style=\"overflow-y:auto;\">\r\n        <img border=\"0\" style=\"margin-top: 2px;\" src=\"{GALLERY_IMAGE_PATH}\" id=\"image\" alt=\"\" /><br />\r\n        {GALLERY_IMAGE_TITLE} {GALLERY_IMAGE_FILESIZE}<br />{GALLERY_IMAGE_DESCRIPTION}\r\n    </div>\r\n    <div class=\"image_navigation\">\r\n        <a title=\"{TXT_GALLERY__PREVIOUS_IMAGE}\" href=\"{GALLERY_IMAGE_PREVIOUS}\"><img alt=\"{TXT_GALLERY__PREVIOUS_IMAGE}\" style=\"border: 0px none ; margin-left: 10px; margin-right: 5px; width: 18px; height: 18px;\" src=\"images/modules/gallery/previous.gif\" /></a>&nbsp; 		<a title=\"{TXT_GALLERY__NEXT_IMAGE}\" href=\"{GALLERY_IMAGE_NEXT}\"><img alt=\"{TXT_GALLERY__NEXT_IMAGE}\" style=\"border: 0px none ; margin-left: 5px; margin-right: 10px; width: 18px; height: 18px;\" src=\"images/modules/gallery/next.gif\" /></a>\r\n    </div>\r\n    \r\n    <!-- BEGIN votingTab -->\r\n    <div class=\"image_voting\">\r\n        <table>\r\n            <tbody>\r\n                <tr>\r\n                    <th><a href=\"javascript:showOrHide(\'votingDiv\');\">{TXT_VOTING_TITLE}</a></th>\r\n                </tr>\r\n            </tbody>\r\n        </table>\r\n        <div id=\"votingDiv\" style=\"width: 100%; display: none; text-align: center;\">\r\n        <table>\r\n            <tbody>\r\n                <tr>\r\n                    <td>\r\n                        <!-- BEGIN showVotingBar --> 						\r\n                        <a href=\"index.php?section=gallery&amp;cid={VOTING_BAR_CID}&amp;pId={VOTING_BAR_PICID}&amp;mark={VOTING_BAR_MARK}\" title=\"{VOTING_BAR_ALT}\"><img align=\"bottom\" src=\"{VOTING_BAR_SRC}\" alt=\"{VOTING_BAR_ALT}\" style=\"border: 0px none ; margin-left: 1px; margin-top: 3px;\" /></a> 												\r\n                        <!-- END showVotingBar -->\r\n                        {TXT_VOTING_ALREADY_VOTED}&nbsp;{TXT_VOTING_STATS_ACTUAL}:&nbsp;&Oslash;&nbsp;<strong>{VOTING_STATS_MARK}</strong>&nbsp;{TXT_VOTING_STATS_WITH}&nbsp;<strong>{VOTING_STATS_VOTES}</strong>&nbsp;{TXT_VOTING_STATS_VOTES} 					\r\n                    </td>\r\n                </tr>\r\n            </tbody>\r\n        </table>\r\n        </div>\r\n    </div>\r\n    <!-- END votingTab -->\r\n    \r\n    <!-- BEGIN commentTab -->\r\n    <div class=\"image_comment\">\r\n        <table>\r\n            <tbody>\r\n                <tr>\r\n                    <th><a href=\"javascript:showOrHide(\'commentDiv\');\">{TXT_COMMENTS_TITLE}</a></th>\r\n                </tr>\r\n            </tbody>\r\n        </table>\r\n        <div id=\"commentDiv\" style=\"width: 100%; display: none; text-align: left;\">\r\n            <form method=\"post\" name=\"frmGalComAdd\" action=\"index.php?section=gallery&amp;pId={GALLERY_PICTURE_ID}\">\r\n            <input type=\"hidden\" name=\"frmGalComAdd_PicId\" value=\"{GALLERY_PICTURE_ID}\" />\r\n            <input type=\"hidden\" value=\"{GALLERY_CATEGORY_ID}\" name=\"frmGalComAdd_GalId\" />\r\n            <table>\r\n                <tbody>\r\n                    <!-- BEGIN showComments -->\r\n                    <tr class=\"row{COMMENTS_ROWCLASS}\">\r\n                            <td width=\"20%\" valign=\"top\" nowrap=\"nowrap\">\r\n                            <strong>{COMMENTS_NAME}</strong><br />\r\n                            <em>{COMMENTS_DATE}</em><br />\r\n                            {COMMENTS_EMAIL}&nbsp;{COMMENTS_WWW}		\r\n                        </td>\r\n                        <td width=\"80%\" valign=\"top\" class=\"row_{COMMENTS_ROWCLASS}\">{COMMENTS_TEXT}</td>\r\n                    </tr>\r\n                    <!-- END showComments -->\r\n                </tbody>\r\n            </table>\r\n            <h2>{TXT_COMMENTS_ADD_TITLE}</h2>\r\n            <p><label for=\"frmGalComAdd_Name\">{TXT_COMMENTS_ADD_NAME}</label><input id=\"frmGalComAdd_Name\" name=\"frmGalComAdd_Name\" value=\"\" type=\"text\"></p>\r\n            <p><label for=\"frmGalComAdd_Email\">{TXT_COMMENTS_ADD_EMAIL}</label><input id=\"frmGalComAdd_Email\" name=\"frmGalComAdd_Email\" value=\"\" type=\"text\"></p>\r\n            <p><label for=\"frmGalComAdd_Homepage\">{TXT_COMMENTS_ADD_HOMEPAGE}</label><input id=\"frmGalComAdd_Homepage\" name=\"frmGalComAdd_Homepage\" value=\"\" type=\"text\"></p>\r\n            <p><label for=\"frmGalComAdd_Text\">{TXT_COMMENTS_ADD_TEXT}</label><input id=\"frmGalComAdd_Name\" name=\"frmGalComAdd_Text\" value=\"\" type=\"text\"></p>\r\n            <p><input type=\"submit\" name=\"frmGalComAdd_Submit\" value=\"{TXT_COMMENTS_ADD_SUBMIT}\" /></p>\r\n            </form>\r\n        </div>\r\n    </div>\r\n    <!-- END commentTab -->\r\n    \r\n    <script language=\"javascript\" type=\"text/javascript\">\r\n	<!-- \r\n	// show and hide blocks\r\n	function showOrHide(strElement)\r\n	{\r\n		if (document.getElementById(strElement).style.display == \'block\'){\r\n			document.getElementById(strElement).style.display = \'none\';\r\n		} else {\r\n			document.getElementById(strElement).style.display = \'block\';\r\n		}\r\n	}\r\n	//-->\r\n	</script>  \r\n\r\n	<!-- END galleryImage -->\r\n	\r\n	<center>{GALLERY_CATEGORY_PAGING}</center>\r\n    <center>{GALLERY_PAGING}</center>\r\n</div>\r\n','Photo gallery','','y',0,'on','system',20,'2');
